        this.acceptorEventLoopGroup = new NioEventLoopGroup(config.getAcceptorThreads());
        this.networkEventLoopGroup = new NioEventLoopGroup(config.getNetworkThreads());
        this.config = config;
        this.applicationContext = applicationContext;
