            if (ApplicationProtocolNames.HTTP_2.equals(protocol)) {
                Http2FrameCodecBuilder builder = Http2FrameCodecBuilder.forServer().initialSettings(Http2SecurityUtil.recommendedServerSettings());
                builder.frameLogger(new Http2FrameLogger(LogLevel.INFO, Http2OrHttpHandler.class));
                ch.pipeline().addLast(builder.build(), new Http2MultiplexHandler(new CustHttp2Handler()));
                return;
            }

            throw new IllegalStateException("unknown protocol: " + protocol);
        }
    }

    class CustHttp2Handler extends ChannelInitializer<SocketChannel> {
        @Override
        protected void initChannel(SocketChannel ch) throws Exception {
            // Add custom handlers for HTTP/2 processing here
        }
    }


    public static void main(String[] args) throws Exception {
        int port = DEFAULT_PORT;
        if (args.length > 0) {
            port = Integer.parseInt(args[0]);
        }

        Http2Server server = new Http2Server();
        server.start(port);
    }
}

