import javax.jms.*;
import org.apache.activemq.ActiveMQConnectionFactory;

public class JmsConsumer {

    private static final String USERNAME = "admin";
    private static final String PASSWORD = "admin";
    private static final String BROKERURL = "tcp://localhost:61616";

    private ConnectionFactory connectionFactory;
    private Connection connection;
    private Session session;
    private Destination destination;
    private MessageConsumer messageConsumer;

    public JmsConsumer() throws JMSException {
        connectionFactory = new ActiveMQConnectionFactory(USERNAME, PASSWORD, BROKERURL);
        connection = connectionFactory.createConnection();
        connection.start();
        session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);
    }

    public void consumeQueueMessages() throws JMSException {
        destination = session.createQueue("SAMPLE_MQ_QUEUE");
        messageConsumer = session.createConsumer(destination);
        while (true) {
            Message message = messageConsumer.receive(5000); // Timeout after 5 seconds
            if (message == null) {
                break; // Exit loop if no message received within timeout
            }
            if (message instanceof TextMessage) {
                TextMessage textMessage = (TextMessage) message;
                System.out.println("Received message from queue: " + textMessage.getText());
            } else {
                System.out.println("Received message of unknown type from queue.");
            }
        }
    }


    public void consumeTopicMessages() throws JMSException {
        destination = session.createTopic("SAMPLE_MQ_TOPIC");
        messageConsumer = session.createConsumer(destination);
        while (true) {
            Message message = messageConsumer.receive(5000); // Timeout after 5 seconds
            if (message == null) {
                break; // Exit loop if no message received within timeout
            }
            if (message instanceof TextMessage) {
                TextMessage textMessage = (TextMessage) message;
                System.out.println("Received message from topic: " + textMessage.getText());
            } else {
                System.out.println("Received message of unknown type from topic.");
            }
        }
    }

    public void closeConnection() throws JMSException {
        if (messageConsumer != null) {
            messageConsumer.close();
        }
        if (session != null) {
            session.close();
        }
        if (connection != null) {
            connection.close();
        }
    }

}

