import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.DelimiterBasedFrameDecoder;
import io.netty.handler.codec.Delimiters;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import java.net.InetAddress;
import java.net.UnknownHostException;
import java.util.HashMap;
import java.util.Map;
import java.util.Random;
import com.google.gson.JsonObject;

public class Console {

    private static Console instance;
    private Map<String, Class<? extends Command>> commands = new HashMap<>();

    private Console() {
        registerCommand("help", HelpCommand.class);
        registerCommand("hello", HelloCommand.class);
        registerCommand("device", DeviceCommand.class);
    }

    public static Console getInstance() {
        if (instance == null) {
            instance = new Console();
        }
        return instance;
    }

    public void listenOnTCP(int port) {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        public void initChannel(SocketChannel ch) {
                            ChannelPipeline p = ch.pipeline();
                            p.addLast(new DelimiterBasedFrameDecoder(8192, Delimiters.lineDelimiter()));
                            p.addLast(new StringDecoder());
                            p.addLast(new StringEncoder());
                            p.addLast(new Adapter());
                        }
                    });
            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            e.printStackTrace();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private void registerCommand(String command, Class<? extends Command> commandClass) {
        commands.put(command, commandClass);
    }


    class Adapter extends ChannelInboundHandlerAdapter {

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            String input = (String) msg;
            String[] tokens = input.trim().split("\\s+");
            if (tokens.length > 0) {
                String commandName = tokens[0];
                Class<? extends Command> commandClass = commands.get(commandName);
                if (commandClass != null) {
                    try {
                        Command command = commandClass.getConstructor(String.class).newInstance(input);
                        String result = command.execute();
                        sendStringL(ctx, result);
                    } catch (Exception e) {
                        sendStringL(ctx, "Error executing command: " + e.getMessage());
                    }
                } else {
                    sendStringL(ctx, "Unknown command: " + commandName);
                }
            }
            sendPrompt(ctx);
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) throws Exception {
            sendStringL(ctx, "Console Server");
            sendPrompt(ctx);
        }

        @Override
        public void channelReadComplete(ChannelHandlerContext ctx) {
            ctx.flush();
        }

        private void sendStringL(ChannelHandlerContext ctx, String text) {
            if (!text.endsWith("\n")) {
                text += "\n";
            }
            sendString(ctx, text);
        }

        private void sendString(ChannelHandlerContext ctx, String text) {
            ctx.writeAndFlush(text);
        }

        private void sendPrompt(ChannelHandlerContext ctx) {
            sendString(ctx, "> ");
        }
    }

    abstract static class Command {
        String command;

        Command(String command) {
            this.command = command;
        }

        abstract String execute();
    }

    static class HelpCommand extends Command {
        HelpCommand(String command) {
            super(command);
        }

        @Override
        String execute() {
            return "Available commands: help, hello, device";
        }
    }

    static class HelloCommand extends Command {
        private static final String[] greetings = {"Hi", "Hello", "Hey", "Howdy"};
        private static final Random random = new Random();

        HelloCommand(String command) {
            super(command);
        }

        @Override
        String execute() {
            return greetings[random.nextInt(greetings.length)] + "!";
        }
    }

    static class DeviceCommand extends Command {
        DeviceCommand(String command) {
            super(command);
        }

        @Override
        String execute() {
            JsonObject deviceInfo = new JsonObject();
            try {
                deviceInfo.addProperty("hostname", InetAddress.getLocalHost().getHostName());
                deviceInfo.addProperty("os", System.getProperty("os.name"));
            } catch (UnknownHostException e) {
                deviceInfo.addProperty("hostname", "unknown");
            }
            return deviceInfo.toString();
        }
    }


    public static void main(String[] args) {
        Console console = Console.getInstance();
        console.listenOnTCP(8080);
    }
}

