import io.netty.handler.timeout.IdleStateHandler;
import java.net.InetSocketAddress;
import java.util.concurrent.atomic.AtomicInteger;
import com.weibo.api.motan.common.URL;
import com.weibo.api.motan.exception.MotanFrameworkException;
import com.weibo.api.motan.rpc.Request;
import com.weibo.api.motan.rpc.Response;
import com.weibo.api.motan.transport.AbstractServer;
import com.weibo.api.motan.transport.MessageHandler;
import com.weibo.api.motan.util.StandardThreadExecutor;


public class NettyServer extends AbstractServer {

    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Channel serverChannel;
    private MessageHandler messageHandler;
    private StandardThreadExecutor standardThreadExecutor;
    private AtomicInteger rejectCounter = new AtomicInteger(0);
    private NettyServerChannelManage channelManage;


    public NettyServer(URL url, MessageHandler messageHandler) {
        super(url);
        this.messageHandler = messageHandler;
        this.channelManage = new NettyServerChannelManage();
    }

    @Override
    public boolean open() {
        if (isAvailable()) {
            return true;
        }

        bossGroup = new NioEventLoopGroup(getUrl().getIntParameter("iothreads", 1));
        workerGroup = new NioEventLoopGroup(getUrl().getIntParameter("threads", 200));

        standardThreadExecutor = new StandardThreadExecutor(getUrl().getIntParameter("threads", 200),
                getUrl().getIntParameter("queues", 0),
                getUrl().getParameter("threadname", "motan-server-worker"));

        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(bossGroup, workerGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast("idleStateHandler", new IdleStateHandler(getUrl().getIntParameter("idleTimeout", 0), 0, 0));
                        pipeline.addLast("decoder", new NettyDecoder());
                        pipeline.addLast("encoder", new NettyEncoder());
                        pipeline.addLast("handler", new NettyServerHandler(NettyServer.this, messageHandler, standardThreadExecutor, channelManage));
                    }
                })
                .option(ChannelOption.SO_BACKLOG, getUrl().getIntParameter("backlog", 1024))
                .childOption(ChannelOption.SO_KEEPALIVE, true)
                .childOption(ChannelOption.TCP_NODELAY, true);

        try {
            ChannelFuture future = bootstrap.bind(new InetSocketAddress(getUrl().getPort())).sync();
            serverChannel = future.channel();
            setState(AVAILABLE);
        } catch (Exception e) {
            throw new MotanFrameworkException("NettyServer bind error: " + getUrl(), e);
        }

        return isAvailable();
    }

    @Override
    public void close() {
        if (isClosing() || isClosed()) {
            return;
        }
        setState(CLOSE);

        try {
            if (serverChannel != null) {
                serverChannel.close().sync();
            }
        } catch (InterruptedException e) {
            // ignore
        }

        try {
            bossGroup.shutdownGracefully().sync();
            workerGroup.shutdownGracefully().sync();
        } catch (InterruptedException e) {
            // ignore
        }

        standardThreadExecutor.shutdown();
        channelManage.close();
    }

    @Override
    public boolean isBound() {
        return isAvailable();
    }

    @Override
    public Response request(Request request) {
        throw new MotanFrameworkException("NettyServer does not support synchronous requests.");
    }
}

