import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.SslHandler;
import io.netty.handler.ssl.util.SelfSignedCertificate;
import javax.net.ssl.SSLEngine;

import java.util.List;

public class PortUnificationServer {

    private static final int PORT = 8080;

    public static void main(String[] args) throws Exception {
        SelfSignedCertificate ssc = new SelfSignedCertificate();
        SslContext sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();

        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        public void initChannel(SocketChannel ch) throws Exception {
                            ch.pipeline().addLast(new PortUnificationHandler(sslCtx));
                        }
                    })
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(PORT).sync();
            f.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }


    private static class PortUnificationHandler extends ByteToMessageDecoder {
        private final SslContext sslCtx;
        private boolean sslEnabled;

        public PortUnificationHandler(SslContext sslCtx) {
            this.sslCtx = sslCtx;
        }

        @Override
        protected void decode(io.netty.channel.ChannelHandlerContext ctx, io.netty.buffer.ByteBuf in, List<Object> out) throws Exception {
            if (!sslEnabled && in.readableBytes() >= 5) {
                if (in.getByte(0) == 22 &&  // SSL/TLS record type 22 (handshake)
                        in.getByte(1) == 3 && // SSL/TLS major version 3
                        (in.getByte(2) == 1 || in.getByte(2) == 2 || in.getByte(2) == 3 || in.getByte(2) == 4) && // Minor version
                        (in.getUnsignedShort(3) > 0 && in.getUnsignedShort(3) < 18432)) { // Length check

                    sslEnabled = true;
                    SSLEngine engine = sslCtx.newEngine(ctx.alloc());
                    engine.setUseClientMode(false);
                    ctx.pipeline().addFirst("ssl", new SslHandler(engine));
                } else {
                    sslEnabled = false; // Plaintext connection
                }
            }
            if (sslEnabled) {
                ctx.pipeline().remove(this);
            }
            in.retain();
            out.add(in);
        }
    }
}

