import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.epoll.*;
import io.netty.channel.kqueue.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.DatagramChannel;
import io.netty.channel.socket.nio.NioDatagramChannel;
import io.netty.util.concurrent.*;
import java.net.InetSocketAddress;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;

public class GeyserServer {

    private static final Transport TRANSPORT = compatibleTransport();
    private static final int SHUTDOWN_QUIET_PERIOD_MS = 2000;
    private static final int SHUTDOWN_TIMEOUT_MS = 15000;

    private final GeyserImpl geyser;
    private final EventLoopGroup group;
    private final EventLoopGroup childGroup;
    private final ServerBootstrap bootstrap;
    private final EventLoopGroup playerGroup;
    private ChannelFuture[] bootstrapFutures;

    public GeyserServer(GeyserImpl geyser, int threadCount) {
        this.geyser = geyser;
        this.group = TRANSPORT.groupFactory().newEventLoopGroup();
        this.childGroup = TRANSPORT.groupFactory().newEventLoopGroup(threadCount);
        this.playerGroup = TRANSPORT.groupFactory().newEventLoopGroup();
        this.bootstrap = createBootstrap();
    }

    public CompletableFuture<Void> bind(InetSocketAddress address) {
        CompletableFuture<Void> future = new CompletableFuture<>();
        this.bootstrapFutures = new ChannelFuture[] { this.bootstrap.bind(address) };

        for (ChannelFuture f : bootstrapFutures) {
            f.addListener((ChannelFutureListener) fl -> {
                if (!fl.isSuccess()) {
                    future.completeExceptionally(fl.cause());
                } else {
                    future.complete(null);
                }
            });
        }
        return future;
    }


    public void shutdown() {
        if (bootstrapFutures != null) {
            for (ChannelFuture future : bootstrapFutures) {
                future.channel().close().syncUninterruptibly();
            }
        }

        group.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS).syncUninterruptibly();
        childGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS).syncUninterruptibly();
        playerGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS).syncUninterruptibly();
    }

    private ServerBootstrap createBootstrap() {
        return new ServerBootstrap()
                .group(group, childGroup)
                .channelFactory(() -> TRANSPORT.channelClass().getConstructor().newInstance())
                .childHandler(new ChannelInitializer<DatagramChannel>() {
                    @Override
                    protected void initChannel(DatagramChannel ch) {
                        ch.pipeline().addLast(new GeyserServerHandler(geyser, playerGroup));
                    }
                })
                .option(ChannelOption.SO_BROADCAST, true);
    }

    private static Transport compatibleTransport() {
        if (isClassAvailable("io.netty.channel.epoll.EpollDatagramChannel")) {
            return new Transport(EpollDatagramChannel.class, Epoll::newEventLoopGroup);
        } else if (isClassAvailable("io.netty.channel.kqueue.KQueueDatagramChannel")) {
            return new Transport(KQueueDatagramChannel.class, KQueue::newEventLoopGroup);
        } else {
            return new Transport(NioDatagramChannel.class, NioEventLoopGroup::new);
        }
    }

    private static boolean isClassAvailable(String className) {
        try {
            Class.forName(className);
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

    private record Transport(Class<? extends DatagramChannel> channelClass, EventLoopGroupFactory groupFactory) {}

    interface EventLoopGroupFactory {
        EventLoopGroup newEventLoopGroup();
        EventLoopGroup newEventLoopGroup(int nThreads);
    }
}

