import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.charset.StandardCharsets;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.ArrayList;
import java.util.List;

public class FileChannelProcessor {

    private static final int BUFFER_SIZE = 500 * 1024 * 1024;
    private final FileChannel channel;
    private List<Buffer> buffers;

    public FileChannelProcessor(Path filePath) throws IOException {
        this.channel = FileChannel.open(filePath, StandardOpenOption.READ);
    }

    public List<Buffer> createBuffers() throws IOException {
        buffers = new ArrayList<>();
        long fileSize = channel.size();
        long position = 0;

        while (position < fileSize) {
            long remaining = fileSize - position;
            int bufferSize = (int) Math.min(remaining, BUFFER_SIZE);
            MappedByteBuffer mappedByteBuffer = channel.map(FileChannel.MapMode.READ_ONLY, position, bufferSize);
            int length = bufferSize;

            if (position + bufferSize < fileSize) {
                length = findLastNewline(mappedByteBuffer, bufferSize);
            }

            buffers.add(new Buffer(mappedByteBuffer, length));
            position += length;
            mappedByteBuffer.clear();

        }
        return buffers;
    }


    private int findLastNewline(ByteBuffer buffer, int bufferSize) {
        int length = bufferSize;
        for (int i = bufferSize - 1; i >= 0; i--) {
            byte b = buffer.get(i);
            if (b == '\n') {
                length = i + 1;
                break;
            }
        }
        return length;
    }


    public void close() throws IOException {
        channel.close();
    }

    record Buffer(ByteBuffer byteBuffer, int length) {}
}

