                .connectTimeout(10, TimeUnit.SECONDS)
                .writeTimeout(10, TimeUnit.SECONDS)
                .readTimeout(30, TimeUnit.SECONDS)
                .build();
    }

    public List<String> scrape(String url, long timeoutMillis) throws IOException, InterruptedException {
        return scrape(url, null, null, timeoutMillis);
    }


    public List<String> scrape(String url, String user, String password, long timeoutMillis) throws IOException, InterruptedException {
        Request.Builder requestBuilder = new Request.Builder()
                .url(url);

        if (user != null && password != null) {
            String credential = Credentials.basic(user, password);
            requestBuilder.header("Authorization", credential);
        }

        Request request = requestBuilder.build();

        List<String> lines = new ArrayList<>();
        int retries = 3;
        while (retries > 0) {
            try (Response response = client.newCall(request).execute()) {
                if (response.isSuccessful() && response.body() != null) {
                    String responseBody = response.body().string();
                    for (String line : responseBody.split("\n")) {
                        lines.add(line);
                    }
                    return lines;
                } else {
                    System.err.println("Request failed: " + response.code() + " " + response.message());
                }
            } catch (IOException e) {
                System.err.println("Error during request: " + e.getMessage());
            }

            retries--;
            if (retries > 0) {
                Thread.sleep(timeoutMillis);
                System.err.println("Retrying...");
            }
        }

        throw new IOException("Failed to scrape URL after multiple retries.");
    }
}

