    private static final Logger log = LoggerFactory.getLogger(AcceptSocketService.class);

    public AcceptSocketService(MessageStoreConfig messageStoreConfig) {
        this.messageStoreConfig = messageStoreConfig;
        this.socketAddressListen = new InetSocketAddress(messageStoreConfig.getHaListenPort());
    }

    public void beginAccept() throws IOException {
        this.serverSocketChannel = ServerSocketChannel.open();
        this.selector = Selector.open();
        this.serverSocketChannel.socket().setReuseAddress(true);
        this.serverSocketChannel.socket().bind(this.socketAddressListen);
        this.serverSocketChannel.configureBlocking(false);
        this.serverSocketChannel.register(this.selector, SelectionKey.OP_ACCEPT);
    }

    public void shutdown(final boolean interrupt) {
        super.shutdown(interrupt);
        try {
            this.serverSocketChannel.close();
            this.selector.close();
        } catch (IOException e) {
            log.error("AcceptSocketService shutdown exception", e);
        }
    }

    public void run() {
        while (!this.isStopped()) {
            try {
                this.selector.select(1000);
                Set<SelectionKey> selected = this.selector.selectedKeys();
                Iterator<SelectionKey> it = selected.iterator();
                while (it.hasNext()) {
                    SelectionKey sk = it.next();
                    it.remove();
                    try {
                        if (sk.isAcceptable()) {
                            doAccept(sk);
                        }
                    } catch (IOException e) {
                        log.error("AcceptSocketService handle exception", e);
                        sk.cancel();
                    }
                }
            } catch (Exception e) {
                log.error("AcceptSocketService run exception", e);
            }
        }
    }

    private void doAccept(SelectionKey sk) throws IOException {
        ServerSocketChannel server = (ServerSocketChannel) sk.channel();
        SocketChannel sc = server.accept();
        if (sc != null) {
            try {
                HAConnection conn = createConnection(sc);
                conn.start();
            } catch (IOException e) {
                log.error("createConnection exception", e);
                sc.close();
            }
        }
    }

    public abstract HAConnection createConnection(SocketChannel sc) throws IOException;


    static class MessageStoreConfig {
        private int haListenPort;

        public int getHaListenPort() {
            return haListenPort;
        }

        public void setHaListenPort(int haListenPort) {
            this.haListenPort = haListenPort;
        }
    }

    static abstract class HAConnection extends ServiceThread {
        public abstract void start();
    }

    static abstract class ServiceThread implements Runnable {
        private volatile boolean stopped = false;
        private Thread thread;


        public synchronized void start() {
            this.thread = new Thread(this, this.getServiceName());
            this.thread.setDaemon(true);
            this.thread.start();
        }

        public String getServiceName() {
            return this.getClass().getSimpleName();
        }

        public void shutdown(final boolean interrupt) {
            this.stopped = true;
            if (interrupt) {
                this.thread.interrupt();
            }
        }

        public boolean isStopped() {
            return stopped;
        }


        public void run() {
        }
    }
}

