import org.apache.http.client.methods.HttpGet;
import org.apache.http.impl.client.CloseableHttpClient;
import org.apache.http.impl.client.HttpClients;
import org.apache.http.util.EntityUtils;


public class NioHttpServer {

    private final Charset charset = StandardCharsets.UTF_8;
    private final CloseableHttpClient client = HttpClients.createDefault();

    public void startServer(int port) throws IOException {
        try (ServerSocketChannel serverSocketChannel = ServerSocketChannel.open()) {
            serverSocketChannel.socket().bind(new InetSocketAddress(port));
            serverSocketChannel.configureBlocking(false);

            Selector selector = Selector.open();
            serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);

            System.out.println("Server started on port " + port);

            while (true) {
                selector.select();
                Iterator<SelectionKey> iterator = selector.selectedKeys().iterator();
                while (iterator.hasNext()) {
                    SelectionKey key = iterator.next();
                    iterator.remove();

                    if (!key.isValid()) {
                        continue;
                    }

                    if (key.isAcceptable()) {
                        handleAccept(key, selector);
                    } else if (key.isReadable()) {
                        handleRead(key, selector);
                    } else if (key.isWritable()) {
                        handleWrite(key, selector);
                    }
                }
            }
        }
    }

    private void handleAccept(SelectionKey key, Selector selector) throws IOException {
        ServerSocketChannel serverSocketChannel = (ServerSocketChannel) key.channel();
        SocketChannel clientChannel = serverSocketChannel.accept();
        clientChannel.configureBlocking(false);
        clientChannel.register(selector, SelectionKey.OP_READ);
    }

    private void handleRead(SelectionKey key, Selector selector) throws IOException {
        SocketChannel clientChannel = (SocketChannel) key.channel();
        ByteBuffer buffer = ByteBuffer.allocate(1024);
        int bytesRead = clientChannel.read(buffer);

        if (bytesRead == -1) {
            clientChannel.close();
            return;
        }

        if (bytesRead > 0) {
            buffer.flip();
            String request = charset.decode(buffer).toString();
            System.out.println("Received request: " + request);

            key.interestOps(SelectionKey.OP_WRITE);
            key.attach(executeResponse());
        }
    }


    private void handleWrite(SelectionKey key, Selector selector) throws IOException {
        SocketChannel clientChannel = (SocketChannel) key.channel();
        String responseBody = (String) key.attachment();

        String response = "HTTP/1.1 200 OK\r\n" +
                "Content-Length: " + responseBody.length() + "\r\n" +
                "Content-Type: text/html; charset=utf-8\r\n\r\n" +
                responseBody;

        ByteBuffer buffer = charset.encode(response);
        clientChannel.write(buffer);

        key.interestOps(SelectionKey.OP_READ);
        key.attach(null);

        if (buffer.hasRemaining()) {
            key.interestOps(SelectionKey.OP_WRITE);
        } else {
            clientChannel.close();
        }
    }

    private String executeResponse() {
        try {
            HttpGet request = new HttpGet("http://www.baidu.com");
            return EntityUtils.toString(client.execute(request).getEntity());
        } catch (IOException e) {
            return "Error fetching data from external service.";
        }
    }


