public abstract class BaseHttp2Server {

    protected final SslContext sslContext;
    private final AtomicBoolean hasReleasedSslContext = new AtomicBoolean(false);
    private final ServerBootstrap bootstrap;
    private final boolean shouldShutDownEventLoopGroup;
    private final ChannelGroup allChannels = new DefaultChannelGroup(GlobalEventExecutor.INSTANCE);


    public BaseHttp2Server(SslContext sslContext, EventLoopGroup eventLoopGroup) {
        this.sslContext = sslContext;
        if (eventLoopGroup == null) {
            this.bootstrap = new ServerBootstrap();
            this.bootstrap.group(new NioEventLoopGroup(), new NioEventLoopGroup());
            shouldShutDownEventLoopGroup = true;
        } else {
            this.bootstrap = new ServerBootstrap().group(eventLoopGroup);
            shouldShutDownEventLoopGroup = false;
        }

        this.bootstrap.channel(NioServerSocketChannel.class);
        this.bootstrap.childHandler(new ChannelInitializer<SocketChannel>() {
            @Override
            protected void initChannel(SocketChannel ch) throws Exception {
                ChannelPipeline p = ch.pipeline();
                SslHandler sslHandler = sslContext.newHandler(ch.alloc());
                p.addLast(sslHandler);
                p.addLast(new ApplicationProtocolNegotiationHandler(ApplicationProtocolNames.HTTP_1_1) {
                    @Override
                    protected void configurePipeline(ChannelHandlerContext ctx, String protocol) {
                        ChannelPipeline p = ctx.pipeline();
                        if (ApplicationProtocolNames.HTTP_2.equals(protocol)) {
                            SSLSession sslSession = sslHandler.engine().getSession();
                            addHandlersToPipeline(sslSession, p);
                            return;
                        }
                        throw new IllegalStateException("unknown protocol: " + protocol);
                    }
                });
                allChannels.add(ch);
            }
        });
    }

    protected abstract void addHandlersToPipeline(SSLSession sslSession, ChannelPipeline p);

    public CompletableFuture<Integer> start(int port) {
        CompletableFuture<Integer> future = new CompletableFuture<>();
        ChannelFuture bindFuture = bootstrap.bind(new InetSocketAddress(port));

        bindFuture.addListener((ChannelFutureListener) channelFuture -> {
            if (channelFuture.isSuccess()) {
                future.complete(((InetSocketAddress) channelFuture.channel().localAddress()).getPort());
            } else {
                future.completeExceptionally(channelFuture.cause());
            }
        });
        return future;
    }

    public CompletableFuture<Void> shutdown() {
        if (hasReleasedSslContext.compareAndSet(false, true)) {
            if (sslContext != null) {
                sslContext.destroy();
            }
        }

        return allChannels.close().addListener((ChannelFutureListener) future -> {
            CompletableFuture<Void> shutdownFuture = new CompletableFuture<>();
            if (shouldShutDownEventLoopGroup) {
                bootstrap.config().group().shutdownGracefully().addListener(finished -> {
                    bootstrap.config().childGroup().shutdownGracefully().addListener(finished2 -> shutdownFuture.complete(null));
                });
            } else {
                shutdownFuture.complete(null);
            }
        }).thenCompose(v -> new CompletableFuture<>());
    }
}

