class Event {
    public final String line;
    public final boolean isError;

    public Event(String line, boolean isError) {
        this.line = line;
        this.isError = isError;
    }
}


class OutputProxy {
    private final InputStream inputStream;
    private final BlockingDeque<Event> deque;
    private final byte delimiter;
    private final int bufferSize;
    private final boolean isError;

    public OutputProxy(InputStream inputStream, BlockingDeque<Event> deque, byte delimiter, int bufferSize, boolean isError) {
        this.inputStream = inputStream;
        this.deque = deque;
        this.delimiter = delimiter;
        this.bufferSize = bufferSize;
        this.isError = isError;
    }

    public void run() {
        try {
            if (delimiter != 0) {
                DelimitedStreamReader reader = new DelimitedStreamReader(inputStream, delimiter);
                String line;
                while ((line = reader.readLine()) != null) {
                    deque.put(new Event(line, isError));
                }
            } else {
                BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
                char[] buffer = new char[bufferSize];
                int charsRead;
                while ((charsRead = reader.read(buffer)) != -1) {
                    deque.put(new Event(new String(buffer, 0, charsRead), isError));
                }

            }
        } catch (IOException | InterruptedException e) {
            // Handle exceptions appropriately, e.g., log the error
            e.printStackTrace();
        }
    }
}


class DelimitedStreamReader {
    private final InputStream inputStream;
    private final byte delimiter;

    public DelimitedStreamReader(InputStream inputStream, byte delimiter) {
        this.inputStream = inputStream;
        this.delimiter = delimiter;
    }

    public String readLine() throws IOException {
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        int b;
        while ((b = inputStream.read()) != -1) {
            if ((byte) b == delimiter) {
                return baos.toString();
            }
            baos.write(b);
        }
        return baos.size() > 0 ? baos.toString() : null;
    }
}


