    private boolean stdOut = true;

    public DaemonHandler(BlockingDeque<Event> deque, String... command) {
        this.deque = deque;
        this.processBuilder = new ProcessBuilder(Arrays.asList(command));
    }

    public void setStdOutListener(boolean stdOut) {
        this.stdOut = stdOut;
    }

    public Process startDelimited() {
        return startDelimited((byte) '\n');
    }

    public Process startDelimited(byte delimiter) {
        try {
            process = processBuilder.start();
            startThreads(delimiter, 0);
            return process;
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        }
    }

    public Process startUndelimited() {
        return startUndelimited(1024);
    }

    public Process startUndelimited(int bufferSize) {
        try {
            process = processBuilder.start();
            startThreads((byte) 0, bufferSize);
            return process;
        } catch (IOException e) {
            e.printStackTrace();
            return null;
        }
    }


    private void startThreads(byte delimiter, int bufferSize) {
        errProxy = new OutputProxy(process.getErrorStream(), deque, delimiter, bufferSize, true);
        errThread = new Thread(errProxy::run);
        errThread.start();

        if (stdOut) {
            outProxy = new OutputProxy(process.getInputStream(), deque, delimiter, bufferSize, false);
            outThread = new Thread(outProxy::run);
            outThread.start();
        }
    }


    public void stop(boolean force) {
        if (process != null) {
            if (force) {
                process.destroyForcibly();
            } else {
                process.destroy();
                try {
                    process.waitFor(10, TimeUnit.SECONDS);
                    if (process.isAlive()) {
                        process.destroyForcibly();
                    }
                } catch (InterruptedException e) {
                    process.destroyForcibly();
                }
            }
        }
    }

    public int getExitCode() {
        if (process != null && !process.isAlive()) {
            return process.exitValue();
        }
        return PROCESS_STOPPED;
    }
}

