    private OptionSpec<String> optFrequency;
    private File perfBinData;
    private File perfParsedData;
    private Collection<String> requestedEventNames;


    public void parseEvents() throws IOException, InterruptedException {
        List<String> command = new ArrayList<>(Arrays.asList("perf", "script", "-i", perfBinData.getAbsolutePath(), "-F", "comm,pid,tid,cpu,time,event,ip,sym,dso,period"));

        ProcessBuilder pb = new ProcessBuilder(command);
        pb.redirectOutput(perfParsedData);
        pb.redirectErrorStream(true);

        Process process = pb.start();

        InputStreamDrainer stdoutDrainer = new InputStreamDrainer(process.getInputStream(), System.out);
        InputStreamDrainer stderrDrainer = new InputStreamDrainer(process.getErrorStream(), System.err);

        new Thread(stdoutDrainer).start();
        new Thread(stderrDrainer).start();

        boolean exited = process.waitFor(1, TimeUnit.MINUTES);

        if (!exited) {
            process.destroyForcibly();
            throw new RuntimeException("perf script timed out");
        }

        if (process.exitValue() != 0) {
            throw new RuntimeException("perf script failed with exit code " + process.exitValue());
        }
    }


    public Collection<String> addJVMInvokeOptions(BenchmarkParams params) {
        List<String> opts = new ArrayList<>();

        opts.add("-XX:+UnlockDiagnosticVMOptions");
        opts.add("-XX:+PreserveFramePointer");

        List<String> perfRecordArgs = new ArrayList<>(Arrays.asList("perf", "record", "-F", sampleFrequency, "-o", perfBinData.getAbsolutePath(), "-e"));
        perfRecordArgs.addAll(requestedEventNames);

        opts.add("-XX:PerfDataInstrumentationArgs=" + String.join(" ", perfRecordArgs));

        return opts;
    }


    private static class InputStreamDrainer implements Runnable {
        private final InputStream inputStream;
        private final OutputStream outputStream;

        public InputStreamDrainer(InputStream inputStream, OutputStream outputStream) {
            this.inputStream = inputStream;
            this.outputStream = outputStream;
        }

        @Override
        public void run() {
            try {
                byte[] buffer = new byte[1024];
                int len;
                while ((len = inputStream.read(buffer)) != -1) {
                    outputStream.write(buffer, 0, len);
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}
