public class FileChannelOutputStream {

    public static OutputStream newOutputStream(FileChannel channel, boolean forceAtClose) {
        return new OutputStream() {
            final WritableByteChannel ch = Channels.newChannel(this);

            @Override
            public void write(int b) throws IOException {
                write(new byte[]{(byte) b});
            }

            @Override
            public void write(byte[] b, int off, int len) throws IOException {
                ByteBuffer buf = ByteBuffer.wrap(b, off, len);
                while (buf.hasRemaining()) {
                    channel.write(buf);
                }
            }

            @Override
            public void close() throws IOException {
                if (forceAtClose) {
                    channel.force(true);
                }
                channel.close();
            }
        };
    }

    public static OutputStream newOutputStreamForceAtClose(Path p, OpenOption... options) throws IOException {
        return newOutputStream(newFileChannel(p, options), true);
    }


    public static FileChannel newFileChannel(Path p, OpenOption... options) throws IOException {
        Set<OpenOption> set = new HashSet<>(Arrays.asList(options));
        if (!set.contains(StandardOpenOption.WRITE) && !set.contains(StandardOpenOption.APPEND)) {
            set.add(StandardOpenOption.CREATE);
            set.add(StandardOpenOption.WRITE);
            set.add(StandardOpenOption.TRUNCATE_EXISTING);
        }
        return FileChannel.open(p, set);
    }
}
