import java.util.List;
import java.util.concurrent.TimeUnit;
import java.util.regex.Pattern;

public class ShellCommand extends AbstractCommand {

    private static final Pattern shellWildcardPattern = Pattern.compile("[*?{}[]<>~]");
    private final String command;
    private final List<String> argv;

    public ShellCommand(String command) {
        this.command = command;
        this.argv = tokenize(command);
    }

    private boolean hasWildcard(String command) {
        return shellWildcardPattern.matcher(command).find();
    }


    private List<String> tokenize(String s) {
        if (hasWildcard(s)) {
            List<String> argv = new ArrayList<>();
            argv.add("/bin/sh");
            argv.add("-c");
            argv.add(s);
            return argv;
        } else {
            return Arrays.asList(s.split("\\s+"));
        }
    }

    @Override
    public void doExecute(ConcurrentTestCommandExecutor executor) throws Exception {
        ProcessBuilder pb = new ProcessBuilder(argv);
        pb.redirectErrorStream(true);
        Process process = pb.start();

        try (InputStream is = process.getInputStream();
             BufferedReader reader = new BufferedReader(new InputStreamReader(is))) {
            String line;
            while ((line = reader.readLine()) != null) {
                System.out.println(line);
            }
        }

        if (!process.waitFor(executor.getTimeout(), TimeUnit.MILLISECONDS)) {
            process.destroyForcibly();
            throw new InterruptedException("Command timed out: " + command);
        }

        if (process.exitValue() != 0) {
            throw new IOException("Command exited with error code " + process.exitValue() + ": " + command);
        }
    }


    // Dummy AbstractCommand and ConcurrentTestCommandExecutor classes for compilation

    private abstract static class AbstractCommand {
        public abstract void doExecute(ConcurrentTestCommandExecutor executor) throws Exception;
    }

