import java.util.concurrent.TimeUnit;
import java.util.logging.Level;
import java.util.logging.Logger;

class DaemonInfo {
    String name;
    int pid;

    public DaemonInfo(String name, int pid) {
        this.name = name;
        this.pid = pid;
    }
}


public class DaemonRegistry implements AutoCloseable {

    private static final Logger LOGGER = Logger.getLogger(DaemonRegistry.class.getName());
    private static final int MAX_LENGTH = 32 * 1024; // 32KB
    private static final long LOCK_TIMEOUT_MS = TimeUnit.SECONDS.toMillis(20);
    private static final Map<Path, Object> locks = new ConcurrentHashMap<>();

    private final Path registryFile;
    private final Object lck;
    private FileChannel channel;
    private MappedByteBuffer buffer;
    private long size;
    private Map<String, DaemonInfo> infosMap = new ConcurrentHashMap<>();


    public DaemonRegistry(Path registryFile) throws IOException {
        this.registryFile = registryFile;
        this.lck = locks.computeIfAbsent(registryFile, k -> new Object());
        this.size = nextPowerOf2(MAX_LENGTH, 4096);
        openOrCreateFile();
    }

    @Override
    public void close() throws IOException {
        synchronized (lck) {
            if (channel != null) {
                channel.close();
                channel = null;
            }
        }
    }

    public List<DaemonInfo> getAll() {
        doUpdate(() -> {}); // Read within the lock
        return new ArrayList<>(infosMap.values());
    }


    private void read() {
        infosMap.clear();
        buffer.position(0);
        while (buffer.position() < buffer.limit()) {
            String name = readString();
            if (name == null) break;
            int pid = buffer.getInt();
            infosMap.put(name, new DaemonInfo(name, pid));
        }
    }

    private void doUpdate(Runnable updater) {
        synchronized (lck) {
            try (FileLock lock = tryLock()) {
                if (lock != null) {
                    read();
                    updater.run();
                    buffer.position(0);
                    for (DaemonInfo info : infosMap.values()) {
                        writeString(info.name);
                        buffer.putInt(info.pid);
                    }
                }
            } catch (IOException | InterruptedException e) {
                LOGGER.log(Level.SEVERE, "Error updating registry", e);
            }
        }
    }


    private FileLock tryLock() throws IOException, InterruptedException {
        FileLock lock = channel.tryLock();
        if (lock == null) {
            if (channel.lock(0L, Long.MAX_VALUE, true).tryLock(LOCK_TIMEOUT_MS, TimeUnit.MILLISECONDS) == null) {
                throw new IOException("Timeout acquiring lock");
            }
        }
        return lock;
    }

    private void reset() {
        infosMap.clear();
        buffer.clear();
    }

    private String readString() {
        int len = buffer.getInt();
        if (len == -1) return null;
        if (len == 0) return "";
        byte[] bytes = new byte[len];
        buffer.get(bytes);
        return new String(bytes, StandardCharsets.UTF_8);
    }

    private void writeString(String str) {
        byte[] bytes = str.getBytes(StandardCharsets.UTF_8);
        buffer.putInt(bytes.length);
        buffer.put(bytes);
    }

    private void openOrCreateFile() throws IOException {
        synchronized (lck) {
            if (Files.exists(registryFile)) {
                channel = FileChannel.open(registryFile, StandardOpenOption.READ, StandardOpenOption.WRITE);
                size = nextPowerOf2(channel.size(), 4096);
            } else {
                channel = FileChannel.open(registryFile, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE);
            }
            buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, size);
        }
    }

    private long nextPowerOf2(long a, long min) {
        long b = min;
        while (b < a) {
            b = b << 1;
        }
        return b;
    }
}

