    public static Process sudoNoWait(Map<String, String> env, String... command) throws IOException {
        ProcessBuilder pb = new ProcessBuilder("su");
        if (env != null) {
            Map<String, String> environment = pb.environment();
            environment.putAll(env);
        }
        pb.redirectErrorStream(true);
        Process p = pb.start();
        OutputStream os = p.getOutputStream();
        for (String single : command) {
            os.write((single + "\n").getBytes());
        }
        os.write("exit\n".getBytes());
        os.flush();
        return p;
    }

    public static String waitFor(String command, Process process) throws IOException, InterruptedException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
        StringBuilder output = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            output.append(line).append("\n");
        }
        process.waitFor();
        if (process.exitValue() != 0) {
            throw new IOException("Error executing command: " + command + "\n" + output.toString());
        }
        return output.toString();
    }

    public static String findCommand(String command) {
        for (String where : BINARY_PLACES) {
            File check = new File(where + command);
            if (check.exists()) {
                return check.getAbsolutePath();
            }
        }
        return null;
    }
}

