import android.content.Context;
import android.content.SharedPreferences;
import android.os.PowerManager;
import android.util.Log;

import java.io.BufferedReader;
import java.io.DataOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class ProcessHandler {

    private Context mContext;
    private boolean mUseRoot;
    private SharedPreferences mPreferences;

    public ProcessHandler(Context context, boolean useRoot, SharedPreferences preferences) {
        mContext = context;
        mUseRoot = useRoot;
        mPreferences = preferences;
    }

    private Process setupAndLaunch(HashMap<String, String> env, String[] command) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(command);
        if (env != null) {
            Map<String, String> pbEnv = pb.environment();
            pbEnv.putAll(env);
        }

        if (mUseRoot) {
            Process process = Runtime.getRuntime().exec("su");
            DataOutputStream os = new DataOutputStream(process.getOutputStream());
            for (String cmd : command) {
                os.writeBytes(cmd + " ");
            }
            os.writeBytes("\n");
            os.writeBytes("exit\n");
            os.flush();
            return process;
        } else {
            return pb.start();
        }
    }


    private Thread log(InputStream is, int priority) {
        Thread thread = new Thread(() -> {
            try (BufferedReader br = new BufferedReader(new InputStreamReader(is))) {
                String line;
                while ((line = br.readLine()) != null) {
                    switch (priority) {
                        case Log.VERBOSE:
                            Log.v("ProcessHandler", line);
                            break;
                        case Log.DEBUG:
                            Log.d("ProcessHandler", line);
                            break;
                        case Log.INFO:
                            Log.i("ProcessHandler", line);
                            break;
                        case Log.WARN:
                            Log.w("ProcessHandler", line);
                            break;
                        case Log.ERROR:
                            Log.e("ProcessHandler", line);
                            break;
                        default:
                            Log.i("ProcessHandler", line);
                    }
                }
            } catch (IOException e) {
                Log.e("ProcessHandler", "Error reading stream", e);
            }
        });
        thread.start();
        return thread;
    }

    public String runProcess(ProcessBuilder pb, boolean returnStdOut) throws IOException, InterruptedException {
        PowerManager pm = (PowerManager) mContext.getSystemService(Context.POWER_SERVICE);
        PowerManager.WakeLock wl = pm.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, "ProcessHandler:runProcess");
        wl.acquire();

        Process process = setupAndLaunch(null, pb.command().toArray(new String[0]));

        Thread errorThread = log(process.getErrorStream(), Log.ERROR);
        Thread outputThread = log(process.getInputStream(), Log.INFO);

        StringBuilder output = new StringBuilder();
        if (returnStdOut) {
            try (BufferedReader br = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                String line;
                while ((line = br.readLine()) != null) {
                    output.append(line).append("\n");
                }
            }
        }

        int exitCode = process.waitFor();

        errorThread.join();
        outputThread.join();

        wl.release();

        if (exitCode != 0) {
            throw new IOException("Process exited with code " + exitCode);
        }

        return returnStdOut ? output.toString() : "";
    }


    public List<String> getProcessPIDs(String processName) throws IOException, InterruptedException {
        List<String> pids = new ArrayList<>();
        Process process;
        if (mUseRoot) {
            process = Runtime.getRuntime().exec("su");
            DataOutputStream os = new DataOutputStream(process.getOutputStream());
            os.writeBytes("pidof " + processName + "\n");
            os.writeBytes("exit\n");
            os.flush();
        } else {
            process = Runtime.getRuntime().exec("pidof " + processName);
        }

        try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
            String line;
            while ((line = reader.readLine()) != null) {
                for (String pid : line.split(" ")) {
                    pids.add(pid);
                }
            }
        }

        process.waitFor();
        return pids;
    }
}
