import java.io.*;
import java.util.*;
import java.util.concurrent.*;

class ExtCmd {

    Map<ExtCmdCondition, String> conditionsWithDescriptions = new HashMap<>();
    Set<ExtCmdEndedListener> extCmdEndedListenerSet = new HashSet<>();
    OutputReceiver outReceiver;
    OutputReceiver errReceiver;
    ErrorReporter errLog;
    long startTime;
    String[] execCommand;
    String execCommandStr;
    boolean logExecution;


    public OutputStream exec(ProcessBuilder.Redirect inputRedirect, File directory, String... command) throws IOException, InterruptedException {
        execCommand = command;
        execCommandStr = String.join(" ", command);
        checkForConditions();

        ProcessBuilder pb = new ProcessBuilder(command);
        if (directory != null) {
            pb.directory(directory);
        }
        pb.redirectInput(inputRedirect);
        Process proc = pb.start();
        startTime = System.currentTimeMillis();

        OutputStream stdIn = proc.getOutputStream();
        outReceiver = new OutputReceiver(proc.getInputStream());
        errReceiver = new OutputReceiver(proc.getErrorStream());

        new Thread(outReceiver).start();
        new Thread(errReceiver).start();

        return stdIn;
    }


    public OutputData syncProcess() throws InterruptedException {
        outReceiver.join();
        errReceiver.join();

        int exitCode = -1; // Placeholder for error case
        try {
            Process proc = Runtime.getRuntime().exec(execCommand);
            exitCode = proc.waitFor();
        } catch (IOException e) {
            if (errLog != null) {
                errLog.reportError(e.getMessage());
            }
        }

        OutputData outputData = new OutputData(outReceiver.getOutput(), errReceiver.getOutput(), exitCode);

        for (ExtCmdEndedListener listener : extCmdEndedListenerSet) {
            listener.processEnded(outputData);
        }

        return outputData;

    }

    private void checkForConditions() throws InterruptedException {
        for (Map.Entry<ExtCmdCondition, String> entry : conditionsWithDescriptions.entrySet()) {
            if (!entry.getKey().isMet()) {
                throw new InterruptedException("Condition not met: " + entry.getValue());
            }
        }
    }


    static class OutputData {
        public final String stdOut;
        public final String stdErr;
        public final int exitCode;

        OutputData(String stdOut, String stdErr, int exitCode) {
            this.stdOut = stdOut;
            this.stdErr = stdErr;
            this.exitCode = exitCode;
        }
    }

    interface ExtCmdCondition {
        boolean isMet();
    }

    interface ExtCmdEndedListener {
        void processEnded(OutputData outputData);
    }

    static class OutputReceiver extends Thread {
        private final InputStream inputStream;
        private final ByteArrayOutputStream baos = new ByteArrayOutputStream();

        OutputReceiver(InputStream inputStream) {
            this.inputStream = inputStream;
        }

        @Override
        public void run() {
            try {
                int n;
                byte[] buffer = new byte[1024];
                while ((n = inputStream.read(buffer)) > 0) {
                    baos.write(buffer, 0, n);
                }
            } catch (IOException e) {
                // Handle error
            }
        }

        public String getOutput() {
            return baos.toString();
        }
    }

    interface ErrorReporter {
        void reportError(String message);
    }
}

