import io.lettuce.core.RedisClient;
import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.api.sync.RedisCommands;
import org.reactivestreams.Publisher;
import reactor.core.publisher.Flux;

import java.util.List;
import java.util.concurrent.atomic.AtomicLong;

public class RedisPublisher {

    private final RedisClient client;
    private final StatefulRedisConnection<String, String> connection;

    public RedisPublisher() {
        client = RedisClient.create("redis://localhost:6379/0");
        connection = client.connect();
        RedisCommands<String, String> syncCommands = connection.sync();
        syncCommands.flushall();
    }

    public Publisher<String> createPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }

        RedisCommands<String, String> syncCommands = connection.sync();
        String listName = "mylist";

        for (long i = 0; i < elements; i++) {
            syncCommands.rpush(listName, String.valueOf(i));
        }

        AtomicLong counter = new AtomicLong(0);
        return Flux.generate(sink -> {
            if (counter.get() < elements) {
                List<String> range = syncCommands.lrange(listName, counter.get(), counter.get());
                if (!range.isEmpty()) {
                    sink.next(range.get(0));
                    counter.incrementAndGet();
                }
            } else {
                sink.complete();
            }
        });
    }


    public void close() {
        connection.close();
        client.shutdown();
    }
}

