import redis.clients.jedis.*;
import redis.clients.jedis.exceptions.JedisConnectionException;
import redis.clients.jedis.exceptions.JedisException;
import redis.clients.util.JedisClusterCRC16;

import java.util.*;
import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.ReentrantReadWriteLock;

public class ClusterConnectionProvider implements AutoCloseable {

    private final JedisClusterInfoCache cache;
    private final ReentrantReadWriteLock lock = new ReentrantReadWriteLock();
    private final Lock readLock = lock.readLock();
    private final Lock writeLock = lock.writeLock();


    public ClusterConnectionProvider(Set<HostAndPort> nodes, JedisClientConfig clientConfig) {
        this.cache = new JedisClusterInfoCache(clientConfig);
        initializeSlotsCache(nodes, clientConfig);
    }

    public void initializeSlotsCache(Set<HostAndPort> startNodes, JedisClientConfig clientConfig) {
        for (HostAndPort hostAndPort : startNodes) {
            try (Jedis connection = new Jedis(hostAndPort.getHost(), hostAndPort.getPort(), clientConfig)) {
                cache.discoverClusterNodesAndSlots(connection);
                return;
            } catch (JedisException e) {
                // try next node
            }
        }
        throw new JedisConnectionException("No reachable node in cluster");
    }


    public Connection getConnection(CommandArguments args) {
        int slot = JedisClusterCRC16.getSlot(args.getKey());
        return getConnectionFromSlot(slot);
    }

    public Connection getConnection() {
        readLock.lock();
        try {
            return cache.getRandomConnection();
        } finally {
            readLock.unlock();
        }
    }

    public Connection getConnectionFromSlot(int slot) {
        readLock.lock();
        try {
            return cache.getConnectionFromSlot(slot);
        } catch (JedisConnectionException e) {
            renewSlotCache();
            return cache.getConnectionFromSlot(slot);
        } finally {
            readLock.unlock();
        }
    }


    public void renewSlotCache() {
        writeLock.lock();
        try {
            if (cache.isRefreshed()) {
                return;
            }
            cache.renewClusterSlots(getConnection());
        } finally {
            writeLock.unlock();
        }
    }


    @Override
    public void close() {
        cache.close();
    }

    public Map<String, ConnectionPool> getConnectionMap() {
        return cache.getConnectionMap();
    }

}
