import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;
import redis.clients.jedis.Connection;
import redis.clients.jedis.exceptions.JedisConnectionException;
import redis.clients.jedis.exceptions.JedisValidationException;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.locks.ReentrantLock;
import java.util.logging.Logger;

public class MultiClusterPooledConnectionProvider {

    private final Logger log = Logger.getLogger(MultiClusterPooledConnectionProvider.class.getName());
    private final Map<Integer, Cluster> multiClusterMap = new ConcurrentHashMap<>();
    private volatile int activeMultiClusterIndex;
    private final ReentrantLock activeClusterIndexLock = new ReentrantLock();
    private volatile boolean lastClusterCircuitBreakerForcedOpen = false;


    public MultiClusterPooledConnectionProvider(MultiClusterClientConfig multiClusterClientConfig) {
        if (multiClusterClientConfig == null) {
            throw new JedisValidationException("MultiClusterClientConfig cannot be null");
        }

        for (MultiClusterClientConfig.ClusterConfig clusterConfig : multiClusterClientConfig.getClusters()) {
            JedisPoolConfig poolConfig = new JedisPoolConfig();
            poolConfig.setMaxTotal(clusterConfig.getMaxPoolSize());
            poolConfig.setMaxIdle(clusterConfig.getMaxIdle());
            poolConfig.setMinIdle(clusterConfig.getMinIdle());

            JedisPool jedisPool = new JedisPool(poolConfig, clusterConfig.getHost(), clusterConfig.getPort(), clusterConfig.getTimeout());
            multiClusterMap.put(clusterConfig.getPriority(), new Cluster(jedisPool));
        }

        activeMultiClusterIndex = multiClusterMap.keySet().stream().min(Integer::compare).orElse(0);
    }

    public Connection getConnection() {
        return getConnection(activeMultiClusterIndex);
    }

    public Connection getConnection(int multiClusterIndex) {
        Cluster cluster = multiClusterMap.get(multiClusterIndex);
        if (cluster == null) {
            throw new JedisConnectionException("No cluster found for index: " + multiClusterIndex);
        }
        try {
            return cluster.getConnection();
        } catch (JedisConnectionException e) {
            log.warning("Failed to get connection from cluster " + multiClusterIndex + ": " + e.getMessage());
            handleFailover(multiClusterIndex);
            return getConnection(); // Retry with the new active cluster
        }
    }


    public void close() {
        Cluster cluster = multiClusterMap.get(activeMultiClusterIndex);
        if (cluster != null) {
            cluster.getConnectionPool().close();
        }
    }

    private void handleFailover(int failedClusterIndex) {
        activeClusterIndexLock.lock();
        try {
            if (activeMultiClusterIndex == failedClusterIndex) {
                int nextActiveIndex = multiClusterMap.keySet().stream()
                        .filter(i -> i > activeMultiClusterIndex)
                        .min(Integer::compare)
                        .orElse(multiClusterMap.keySet().stream().min(Integer::compare).orElse(0));

                activeMultiClusterIndex = nextActiveIndex;
                log.info("Failed over to cluster: " + activeMultiClusterIndex);
            }
        } finally {
            activeClusterIndexLock.unlock();
        }
    }


    private class Cluster {
        private final JedisPool connectionPool;

        public Cluster(JedisPool connectionPool) {
            this.connectionPool = connectionPool;
        }

        public Connection getConnection() {
            return connectionPool.getResource();
        }

        public JedisPool getConnectionPool() {
            return connectionPool;
        }
    }
}

