public class RedisPubSubManager {

    private JedisPool jedisPool;
    private String channelName;
    private Logger logger = Logger.getLogger(RedisPubSubManager.class.getName());
    private ExecutorService executor = Executors.newCachedThreadPool();

    public RedisPubSubManager(String host, int port, String channelName) {
        this.channelName = channelName;
        jedisPool = new JedisPool(new JedisPoolConfig(), host, port);
    }

    public void startSubscriber(Subscriber subscriber) {
        executor.submit(() -> {
            try (Jedis jedis = jedisPool.getResource()) {
                jedis.subscribe(new JedisPubSub() {
                    @Override
                    public void onMessage(String channel, String message) {
                        subscriber.onMessage(message);
                    }
                }, channelName);
            } catch (Exception e) {
                logger.log(Level.SEVERE, "Error in subscriber", e);
            }
        });
    }


    public void startPublisher() {
        executor.submit(() -> {
           try (Jedis jedis = jedisPool.getResource()){
               while (true) {
                   try {
                       Thread.sleep(1000); // Example: Publish every second
                       String message = "Message from publisher"; // Replace with your message logic
                       jedis.publish(channelName, message);
                   } catch (InterruptedException e) {
                       Thread.currentThread().interrupt();
                       break;
                   } catch (Exception e) {
                       logger.log(Level.SEVERE, "Error in publisher", e);
                       break; // Exit loop on error
                   }
               }
           }
        });

    }

