import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.ByteOrder;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import javax.sound.sampled.AudioFormat;

public class WaveWriter implements AutoCloseable {

    private final AudioFormat mAudioFormat;
    private int mFileRolloverCounter = 0;
    private final long mMaxSize;
    private Path mFile;
    private FileChannel mFileChannel;
    private boolean mDataChunkOpen = false;
    private long mDataChunkSizeOffset;
    private int mDataChunkSize;


    public WaveWriter(AudioFormat format, Path file, long maxSize) throws IOException {
        mAudioFormat = format;
        mFile = file;
        mMaxSize = maxSize;
        open();
    }

    public WaveWriter(AudioFormat format, Path file) throws IOException {
        this(format, file, 0);
    }

    private void open() throws IOException {
        mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE, StandardOpenOption.WRITE);
        mFileChannel.write(getWaveHeader(mAudioFormat));
        mFileChannel.write(getFormatChunk(mAudioFormat));
        openDataChunk();
    }

    @Override
    public void close() throws IOException {
        close(null);
    }

    public void close(Path path) throws IOException {
        closeDataChunk();
        updateTotalSize();
        if (mFileChannel != null) {
            mFileChannel.close();
            mFileChannel = null;
        }
        if(path != null) {
            mFile.toFile().renameTo(path.toFile());
        }
    }

    public synchronized void writeData(ByteBuffer buffer) throws IOException {
        if (mMaxSize > 0 && mFileChannel.size() + buffer.remaining() > mMaxSize) {
            rollover();
        }
        mDataChunkSize += buffer.remaining();
        mFileChannel.write(buffer);
    }

    private void closeDataChunk() throws IOException {
        if (mDataChunkOpen) {
            updateDataChunkSize();
            mDataChunkOpen = false;
        }
    }

    private void openDataChunk() throws IOException {
        mFileChannel.write(getDataHeader());
        mDataChunkSizeOffset = mFileChannel.position();
        mFileChannel.write(getUnsignedIntegerBuffer(0)); // Placeholder for data chunk size
        mDataChunkSize = 0;
        mDataChunkOpen = true;
    }

    private void rollover() throws IOException {
        closeDataChunk();
        updateTotalSize();
        mFileChannel.close();
        updateFileName();
        open();
    }

    private void updateTotalSize() throws IOException {
        long fileSize = mFileChannel.size();
        mFileChannel.position(4);
        mFileChannel.write(getUnsignedIntegerBuffer(fileSize - 8));
    }

    private void updateDataChunkSize() throws IOException {
        mFileChannel.position(mDataChunkSizeOffset);
        mFileChannel.write(getUnsignedIntegerBuffer(mDataChunkSize));
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        return ByteBuffer.allocate(4).order(ByteOrder.LITTLE_ENDIAN).putInt((int) size);
    }

    private void updateFileName() {
        String originalFileName = mFile.getFileName().toString();
        String baseName = originalFileName.substring(0, originalFileName.lastIndexOf('.'));
        String extension = originalFileName.substring(originalFileName.lastIndexOf('.'));
        mFile = mFile.resolveSibling(baseName + "_" + (++mFileRolloverCounter) + extension);
    }


    public static ByteBuffer getDataHeader() {
        return ByteBuffer.wrap("data".getBytes());
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(44).order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("RIFF".getBytes());
        buffer.putInt(0); // Placeholder for total file size
        buffer.put("WAVE".getBytes());
        return buffer;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        int byteRate = (int) (format.getSampleRate() * format.getFrameSize());
        ByteBuffer buffer = ByteBuffer.allocate(24).order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("fmt ".getBytes());
        buffer.putInt(16); // Subchunk1Size
        buffer.putShort((short) 1); // AudioFormat PCM
        buffer.putShort((short) format.getChannels());
        buffer.putInt((int) format.getSampleRate());
        buffer.putInt(byteRate);
        buffer.putShort((short) format.getFrameSize());
        buffer.putShort((short) (format.getSampleSizeInBits() * format.getChannels()));
        return buffer;
    }
}

