import redis.clients.jedis.util.Hashing;
import java.util.List;
import java.util.SortedMap;
import java.util.TreeMap;

public class JedisSharding {

    private final List<HostAndPort> shards;
    private final JedisClientConfig clientConfig;
    private final Hashing hashing;
    private final SortedMap<Long, Jedis> nodes = new TreeMap<>();

    public JedisSharding(List<HostAndPort> shards, JedisClientConfig clientConfig) {
        this(shards, clientConfig, Hashing.MURMUR_HASH);
    }

    public JedisSharding(List<HostAndPort> shards, JedisClientConfig clientConfig, Hashing hashing) {
        this.shards = shards;
        this.clientConfig = clientConfig;
        this.hashing = hashing;
        initializeShards();
    }

    private void initializeShards() {
        for (HostAndPort shard : shards) {
            long hash = hashing.hash(shard.toString());
            nodes.put(hash, new Jedis(shard, clientConfig));
        }
    }

    public String set(String key, String value) {
        return getShard(key).set(key, value);
    }

    public String get(String key) {
        return getShard(key).get(key);
    }

    public Object sendCommand(String key, Protocol.Command command, String... args) {
        return getShard(key).sendCommand(command, args);
    }


    public void close() {
        // No-op. Connections are managed per-operation.
    }

    private Jedis getShard(String key) {
        long hash = hashing.hash(key);
        SortedMap<Long, Jedis> tailMap = nodes.tailMap(hash);
        long nodeHash = tailMap.isEmpty() ? nodes.firstKey() : tailMap.firstKey();
        return nodes.get(nodeHash);
    }

    private int getShardIndex(String key) {
        return shards.indexOf(getShard(key).getClient().getHostAndPort());
    }
}
