import java.net.SocketAddress;

public class RedisSocketConnector {

    private RedisClient redisClient;
    private StatefulRedisConnection<String, String> connection;

    public void connect(RedisURI redisURI) {
        redisClient = RedisClient.create(redisURI);
        connection = redisClient.connect();
    }

    public void performRedisAction(String key, String value) {
        RedisCommands<String, String> syncCommands = connection.sync();
        syncCommands.set(key, value);
        String retrievedValue = syncCommands.get(key);
        System.out.println("Retrieved value: " + retrievedValue);
    }

    public void close() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown();
        }
    }

    public static RedisURI createSocketRedisUri(String socketPath) {
        return RedisURI.create(RedisURI.URI_SCHEME_REDIS_SOCKET + "://" + socketPath);
    }

    public static boolean isSupported() {
        try {
            Class.forName("java.net.UnixDomainSocketAddress");
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

}

