public class SentineledConnectionProvider {

    private volatile HostAndPort currentMaster;
    private volatile JedisPool pool;
    private final String masterName;
    private final JedisClientConfig masterClientConfig;
    private final JedisClientConfig sentinelClientConfig;
    private final AtomicBoolean running = new AtomicBoolean(true);
    private Thread listenerThread;


    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig, Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.masterClientConfig = masterClientConfig;
        this.sentinelClientConfig = sentinelClientConfig;
        HostAndPort initialMaster = initSentinels(sentinels);
        initMaster(initialMaster);
    }

    public Connection getConnection() {
        if (pool == null) {
            throw new JedisConnectionException("Connection pool not initialized");
        }
        return pool.getResource();
    }

    private synchronized void initMaster(HostAndPort master) {
        if (master.equals(currentMaster)) return;

        currentMaster = master;
        if (pool != null) {
            pool.close();
        }
        pool = new JedisPool(masterClientConfig, master.getHost(), master.getPort());
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        HostAndPort master = null;
        for (HostAndPort sentinel : sentinels) {
            try (Jedis sentinelJedis = new Jedis(sentinelClientConfig, sentinel.getHost(), sentinel.getPort())) {
                master = HostAndPort.parseString(sentinelJedis.sentinelGetMasterAddrByName(masterName)[0] + ":" + sentinelJedis.sentinelGetMasterAddrByName(masterName)[1]);
                listenerThread = new Thread(new SentinelListener(sentinel));
                listenerThread.start();
                break; // Found a working sentinel, no need to check others
            } catch (JedisConnectionException e) {
                // Try the next sentinel
            }
        }
        if (master == null) {
            throw new JedisConnectionException("Could not connect to any sentinel");
        }
        return master;
    }


    private class SentinelListener implements Runnable {
        private final HostAndPort node;

        SentinelListener(HostAndPort node) {
            this.node = node;
        }

        @Override
        public void run() {
            try (Jedis sentinelJedis = new Jedis(sentinelClientConfig, node.getHost(), node.getPort())) {
                sentinelJedis.subscribe(new JedisPubSub() {
                    @Override
                    public void onMessage(String channel, String message) {
                        if ("+switch-master".equals(channel)) {
                            String[] parts = message.split(" ");
                            if (masterName.equals(parts[0])) {
                                HostAndPort newMaster = HostAndPort.parseString(parts[3] + ":" + parts[4]);
                                initMaster(newMaster);
                            }
                        }
                    }
                }, "+switch-master");
            } catch (Exception e) {
                if (running.get()) {
                    // Reconnect logic could be added here
                    System.err.println("Sentinel listener disconnected: " + e.getMessage());
                }
            }
        }
    }

    public void shutdown() {
        running.set(false);
        if (listenerThread != null) {
            listenerThread.interrupt();
        }
        if (pool != null) {
            pool.close();
        }
    }
}
