import org.apache.commons.pool2.impl.GenericObjectPoolConfig;

import java.time.Duration;

public class RedisConnectionManager {

    private ClusterClientOptions clientOptions;
    private TokenBasedRedisCredentialsProvider credentialsProvider;
    private RedisClient client;
    private RedisClusterClient clusterClient;
    private GenericObjectPool<StatefulRedisConnection<String, String>> standalonePool;
    private GenericObjectPool<StatefulRedisClusterConnection<String, String>> clusterPool;


    public void initializeConnection(EntraIdTestContext testCtx) {
        String redisHost = testCtx.getConfiguration().getString("redis.host");
        int redisPort = testCtx.getConfiguration().getInt("redis.port");
        String redisPassword = testCtx.getConfiguration().getString("redis.password");
        boolean redisClusterEnabled = testCtx.getConfiguration().getBoolean("redis.cluster.enabled");

        credentialsProvider = new TokenBasedRedisCredentialsProvider(() -> redisPassword);

        if (redisClusterEnabled) {
            clientOptions = ClusterClientOptions.builder()
                    .topologyRefreshOptions(ClusterTopologyRefreshOptions.builder()
                            .enablePeriodicRefresh(Duration.ofSeconds(30))
                            .build())
                    .build();

            clusterClient = RedisClusterClient.create(RedisURI.Builder.redis(redisHost, redisPort)
                    .withPassword(redisPassword).build());

            GenericObjectPoolConfig<StatefulRedisClusterConnection<String, String>> clusterPoolConfig = new GenericObjectPoolConfig<>();
            clusterPoolConfig.setMaxTotal(10); // Example pool size
            clusterPool = ConnectionPoolSupport.createGenericObjectPool(clusterClient::connect, clusterPoolConfig);

        } else {
            client = RedisClient.create(RedisURI.Builder.redis(redisHost, redisPort)
                    .withPassword(redisPassword).build());

            GenericObjectPoolConfig<StatefulRedisConnection<String, String>> standalonePoolConfig = new GenericObjectPoolConfig<>();
            standalonePoolConfig.setMaxTotal(10); // Example pool size
            standalonePool = ConnectionPoolSupport.createGenericObjectPool(client::connect, standalonePoolConfig);
        }
    }


    public StatefulRedisConnection<String, String> createStandaloneConnection() {
        try {
            return standalonePool.borrowObject();
        } catch (Exception e) {
            throw new RuntimeException("Error creating standalone Redis connection", e);
        }
    }

    public StatefulRedisClusterConnection<String, String> createClusterConnection() {
        try {
            return clusterPool.borrowObject();
        } catch (Exception e) {
            throw new RuntimeException("Error creating cluster Redis connection", e);
        }
    }


    public StatefulRedisPubSubConnection<String, String> createPubSubConnection() {
        if (client != null) {
            return client.connectPubSub();
        } else if (clusterClient != null) {
            return clusterClient.connectPubSub();
        } else {
            throw new IllegalStateException("Redis client not initialized");
        }
    }


