        if (standalonePool != null) {
            standalonePool.close();
        }
        if (clusterPool != null) {
            clusterPool.close();
        }
        if (client != null) {
            client.shutdown();
        }
        if (clusterClient != null) {
            clusterClient.shutdown();
        }
    }

