import java.nio.channels.FileChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.Arrays;

public class ArrayLongLongMapMmap implements AutoCloseable {

    private static final int SEGMENT_BITS = 27;
    private static final int SEGMENT_SIZE = 1 << SEGMENT_BITS;
    private static final long SEGMENT_MASK = SEGMENT_SIZE - 1;

    private final Path path;
    private FileChannel writeChannel;
    private MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;


    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = FileChannel.open(path, StandardOpenOption.READ, StandardOpenOption.WRITE, StandardOpenOption.CREATE);
    }

    public void init() throws IOException {
        long size = writeChannel.size();
        int segments = (int) ((size + SEGMENT_SIZE - 1) / SEGMENT_SIZE);
        segmentsArray = new MappedByteBuffer[segments];

        writeChannel.close();
        this.writeChannel = null;

        this.readChannel = FileChannel.open(path, StandardOpenOption.READ);

        for (int i = 0; i < segments; i++) {
            long position = (long) i * SEGMENT_SIZE;
            long remaining = size - position;
            long mapSize = Math.min(remaining, SEGMENT_SIZE);
            segmentsArray[i] = readChannel.map(FileChannel.MapMode.READ_ONLY, position, mapSize);
        }
    }

    public long get(long key) {
        int segmentIndex = (int) (key >>> SEGMENT_BITS);
        int offset = (int) (key & SEGMENT_MASK);
        offset *= Long.BYTES;

        if (segmentIndex >= segmentsArray.length || offset >= segmentsArray[segmentIndex].capacity()) {
            return -1;
        }

        return segmentsArray[segmentIndex].getLong(offset);
    }


    @Override
    public void close() throws IOException {
        if (segmentsArray != null) {
            for (MappedByteBuffer buffer : segmentsArray) {
                if (buffer != null) {
                    java.lang.reflect.Method cleanerMethod = null;
                    try {
                        cleanerMethod = buffer.getClass().getMethod("cleaner");
                        cleanerMethod.setAccessible(true);
                        Object cleaner = cleanerMethod.invoke(buffer);
                        cleaner.getClass().getMethod("clean").invoke(cleaner);
                    } catch (ReflectiveOperationException e) {
                        // Handle or log the exception appropriately
                        e.printStackTrace();
                    }
                }
            }
            Arrays.fill(segmentsArray, null);
        }

        if (readChannel != null) {
            readChannel.close();
        }

        if (writeChannel != null) {
            writeChannel.close();
        }

        Files.deleteIfExists(path);
    }
}
