import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.Executor;

class PathResource {

    private final Path file;
    private final PathResourceManager manager;

    PathResource(Path file, PathResourceManager manager) {
        this.file = file;
        this.manager = manager;
    }

    void serve(Sender sender, HttpServerExchange exchange, IoCallback callback) {
        serveImpl(sender, exchange, 0L, -1L, callback, false);
    }

    void serveRange(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) {
        serveImpl(sender, exchange, start, end, callback, true);
    }

    Long getContentLength() {
        try {
            return file.toFile().length();
        } catch (SecurityException e) {
            return null;
        }
    }

    private void serveImpl(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback, boolean range) {
        try (FileChannel channel = FileChannel.open(file, StandardOpenOption.READ)) {
            long length = channel.size();
            if (range && end == -1) {
                end = length - 1;
            }
            if (range) {
                if (end >= length) {
                    end = length - 1;
                }
                if (start > end) {
                    exchange.setStatusCode(416);
                    callback.onCompleted(exchange, null);
                    return;
                }
                exchange.getResponseHeaders().put("Content-Range", "bytes " + start + "-" + end + "/" + length);
                length = end - start + 1;
            }
            exchange.setResponseContentLength(length);
            Executor executor = exchange.getAttachment(AttachmentKey.EXECUTOR);
            BaseFileTask task = length > manager.transferThreshold() ? new TransferTask(channel, start, length, exchange, sender, callback) : new ServerTask(channel, start, length, exchange, sender, callback);
            executor.execute(task);
        } catch (IOException e) {
            callback.onFailed(exchange, e);
        }
    }

    private abstract class BaseFileTask implements Runnable {
        protected final FileChannel channel;
        protected final long start;
        protected final long length;
        protected final HttpServerExchange exchange;
        protected final Sender sender;
        protected final IoCallback callback;

        BaseFileTask(FileChannel channel, long start, long length, HttpServerExchange exchange, Sender sender, IoCallback callback) {
            this.channel = channel;
            this.start = start;
            this.length = length;
            this.exchange = exchange;
            this.sender = sender;
            this.callback = callback;
        }
    }

    private final class ServerTask extends BaseFileTask {
        ServerTask(FileChannel channel, long start, long length, HttpServerExchange exchange, Sender sender, IoCallback callback) {
            super(channel, start, length, exchange, sender, callback);
        }

        @Override
        public void run() {
            PooledByteBuffer pooled = exchange.getConnection().getByteBufferPool().allocate();
            ByteBuffer buffer = pooled.getResource();
            try {
                channel.position(start);
                long remaining = length;
                while (remaining > 0) {
                    int res = channel.read(buffer);
                    if (res == -1) {
                        callback.onFailed(exchange, new IOException("Unexpected EOF"));
                        return;
                    }
                    buffer.flip();
                    int toWrite = (int) Math.min(remaining, buffer.remaining());
                    sender.send(buffer, exchange, new WriteCallback(pooled, remaining - toWrite == 0));
                    buffer.clear();
                    remaining -= toWrite;
                }
            } catch (IOException e) {
                callback.onFailed(exchange, e);
            }
        }

        private class WriteCallback implements IoCallback {
            private final PooledByteBuffer pooled;
            private final boolean last;

            WriteCallback(PooledByteBuffer pooled, boolean last) {
                this.pooled = pooled;
                this.last = last;
            }

            @Override
            public void onCompleted(HttpServerExchange exchange, Sender sender) {
                pooled.close();
                if (last) {
                    callback.onCompleted(exchange, sender);
                }
            }

            @Override
            public void onFailed(HttpServerExchange exchange, Exception exception) {
                pooled.close();
                callback.onFailed(exchange, exception);
            }
        }
    }

    private final class TransferTask extends BaseFileTask {
        TransferTask(FileChannel channel, long start, long length, HttpServerExchange exchange, Sender sender, IoCallback callback) {
            super(channel, start, length, exchange, sender, callback);
        }

        @Override
        public void run() {
            try {
                sender.transferFrom(channel, exchange, start, length, callback);
            } catch (IOException e) {
                callback.onFailed(exchange, e);
            }
        }
    }

    interface Sender {
        void send(ByteBuffer buffer, HttpServerExchange exchange, IoCallback callback);
        void transferFrom(FileChannel channel, HttpServerExchange exchange, long start, long length, IoCallback callback) throws IOException;
    }

    interface IoCallback {
        void onCompleted(HttpServerExchange exchange, Sender sender);
        void onFailed(HttpServerExchange exchange, Exception exception);
    }

    interface AttachmentKey<T> {}

    interface HttpServerExchange {
        void setStatusCode(int code);
        void setResponseContentLength(long length);
        <T> T getAttachment(AttachmentKey<T> key);
        Headers getResponseHeaders();
        Connection getConnection();
    }

    interface Headers {
        void put(String key, String value);
    }

    interface Connection {
        ByteBufferPool getByteBufferPool();
    }

    interface ByteBufferPool {
        PooledByteBuffer allocate();
    }

    interface PooledByteBuffer extends AutoCloseable {
        ByteBuffer getResource();
    }

    static class PathResourceManager {
        long transferThreshold() { return 8192; }
    }
}

