import org.apache.flink.api.common.serialization.KeyValueSerializationSchema;
import org.apache.flink.configuration.Configuration;
import org.apache.flink.streaming.api.functions.sink.RichSinkFunction;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.common.message.Message;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.ArrayList;
import java.util.List;
import java.util.Properties;

public class RocketMQSink<IN> extends RichSinkFunction<IN> {

    private static final Logger LOG = LoggerFactory.getLogger(RocketMQSink.class);

    private DefaultMQProducer producer;
    private boolean async;
    private Properties props;
    private TopicSelector<IN> topicSelector;
    private KeyValueSerializationSchema<IN> serializationSchema;
    private boolean batchFlushOnCheckpoint;
    private int batchSize;
    private List<Message> batchList;
    private int messageDeliveryDelayLevel;


    public RocketMQSink(KeyValueSerializationSchema<IN> serializationSchema, TopicSelector<IN> topicSelector, Properties props) {
        this.serializationSchema = serializationSchema;
        this.topicSelector = topicSelector;
        this.props = props;
        this.async = Boolean.parseBoolean(props.getProperty("async", "false"));
        this.batchFlushOnCheckpoint = Boolean.parseBoolean(props.getProperty("batchFlushOnCheckpoint", "false"));
        this.batchSize = Integer.parseInt(props.getProperty("batchSize", "1"));
        this.messageDeliveryDelayLevel = Integer.parseInt(props.getProperty("messageDeliveryDelayLevel", "0"));

        if (batchFlushOnCheckpoint) {
            batchList = new ArrayList<>(batchSize);
        }
    }

    @Override
    public void open(Configuration parameters) throws Exception {
        producer = new DefaultMQProducer(props.getProperty("producerGroup"));
        producer.setNamesrvAddr(props.getProperty("namesrvAddr"));
        producer.start();
    }

    @Override
    public void invoke(IN value, Context context) throws Exception {
        Message msg = prepareMessage(value);
        if (batchFlushOnCheckpoint) {
            batchList.add(msg);
            if (batchList.size() >= batchSize) {
                flushSync();
            }
        } else if (async) {
            producer.send(msg, null);
        } else {
            producer.send(msg);
        }
    }


    private Message prepareMessage(IN value) throws Exception {
        byte[] key = serializationSchema.serializeKey(value);
        byte[] val = serializationSchema.serializeValue(value);
        String topic = topicSelector.getTopic(value);
        String tag = topicSelector.getTag(value);

        Message msg = new Message(topic, tag, key, val);
        if (messageDeliveryDelayLevel > 0) {
            msg.setDelayTimeLevel(messageDeliveryDelayLevel);
        }
        return msg;
    }


    @Override
    public void close() throws Exception {
        if (producer != null) {
            producer.shutdown();
        }
    }

    private void flushSync() throws Exception {
        if (!batchList.isEmpty()) {
            producer.send(batchList);
            batchList.clear();
        }
    }

    public interface TopicSelector<T> {
        String getTopic(T record);
        String getTag(T record);
    }
}

