import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.client.producer.*;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.common.message.MessageQueue;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.atomic.AtomicInteger;

public class RMQAsyncSendProducer extends AbstractMQProducer {

    private static final Logger logger = LoggerFactory.getLogger(RMQAsyncSendProducer.class);
    private final String nsAddr;
    private DefaultMQProducer producer;
    private SendCallback sendCallback;
    private List<SendResult> successSendResult = new ArrayList<>();
    private AtomicInteger exceptionMsgCount = new AtomicInteger(0);
    private int msgSize;


    public RMQAsyncSendProducer(String nsAddr, String topic) {
        this.nsAddr = nsAddr;
        this.topic = topic;
    }

    @Override
    public void create() {
        producer = new DefaultMQProducer(this.getClass().getSimpleName());
        producer.setNamesrvAddr(nsAddr);
        sendCallback = new SendCallback() {
            @Override
            public void onSuccess(SendResult sendResult) {
                successSendResult.add(sendResult);
            }

            @Override
            public void onException(Throwable e) {
                exceptionMsgCount.incrementAndGet();
                logger.error("Async send msg failed.", e);
            }
        };
    }

    @Override
    public void start() {
        try {
            producer.start();
        } catch (MQClientException e) {
            logger.error("Producer start failed.", e);
            throw new RuntimeException(e);
        }
    }


    public void asyncSend(Object msg) {
        Message message = new Message(topic, msg.toString().getBytes());
        try {
            producer.send(message, sendCallback);
        } catch (Exception e) {
            exceptionMsgCount.incrementAndGet();
            logger.error("Async send msg failed.", e);
        }
    }

    public void asyncSend(int msgSize) {
        this.msgSize = msgSize;
        for (int i = 0; i < msgSize; i++) {
            asyncSend(i);
        }
    }

    public void asyncSend(Object msg, MessageQueueSelector selector, Object arg) {
        Message message = new Message(topic, msg.toString().getBytes());
        try {
            producer.send(message, selector, arg, sendCallback);
        } catch (Exception e) {
            exceptionMsgCount.incrementAndGet();
            logger.error("Async send msg failed.", e);
        }
    }


    public void waitForResponse(int timeoutMills) {
        long start = System.currentTimeMillis();
        while (System.currentTimeMillis() - start < timeoutMills) {
            if (msgSize == successSendResult.size() + exceptionMsgCount.get()) {
                break;
            }
        }
    }

    @Override
    public void shutdown() {
        producer.shutdown();
    }
}


