import java.net.*;

public class GSSClient {

    private Socket s;

    public static void main(String[] args) {
        String server = "localhost";
        int port = 11115;
        String mech = "1.2.840.113554.1.2.2";

        try {
            GSSClient client = new GSSClient();
            Oid mechOid = new Oid(mech);
            GSSContext ctxt = client.createCtxt(server, mechOid);

            client.sendMsgToPeer(ctxt, "Hello Server!");
            client.verifyMsgFromPeer(ctxt, "Hello Client!");

            ctxt.dispose();
            client.exit(0);

        } catch (Exception e) {
            e.printStackTrace();
            System.exit(1);
        }
    }


    private GSSContext createCtxt(String serverName, Oid mechOid) throws GSSException, IOException, UnknownHostException {
        s = new Socket(serverName, 11115);

        GSSManager manager = GSSManager.getInstance();
        GSSName server = manager.createName("host@" + serverName, GSSName.NT_HOSTBASED_SERVICE);

        GSSContext ctxt = manager.createContext(server, mechOid, null, GSSContext.DEFAULT_LIFETIME);
        ctxt.requestMutualAuth(true);
        ctxt.requestConf(true);
        ctxt.requestInteg(true);

        byte[] token = new byte[0];

        while (!ctxt.isEstablished()) {
            token = ctxt.initSecContext(token, 0, token.length);

            if (token != null && token.length != 0) {
                OutputStream out = s.getOutputStream();
                DataOutputStream dos = new DataOutputStream(out);
                dos.writeInt(token.length);
                dos.write(token);
                dos.flush();
            }

            if (!ctxt.isEstablished()) {
                InputStream in = s.getInputStream();
                DataInputStream dis = new DataInputStream(in);
                int len = dis.readInt();
                token = new byte[len];
                dis.readFully(token);
            }
        }
        return ctxt;
    }

    private void sendMsgToPeer(GSSContext aCtxt, String msg) throws GSSException, IOException {
        byte[] token = aCtxt.wrap(msg.getBytes(), 0, msg.length(), null);
        OutputStream out = s.getOutputStream();
        DataOutputStream dos = new DataOutputStream(out);
        dos.writeInt(token.length);
        dos.write(token);
        dos.flush();
    }

    private void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws GSSException, IOException {
        InputStream in = s.getInputStream();
        DataInputStream dis = new DataInputStream(in);
        int len = dis.readInt();
        byte[] token = new byte[len];
        dis.readFully(token);

        byte[] bytes = aCtxt.unwrap(token, 0, token.length, null);
        String receivedMsg = new String(bytes);

        if (!receivedMsg.equals(msg)) {
            throw new RuntimeException("Received message does not match expected message.");
        }
    }


    private void usage() {
        print("Usage: java GSSClient <serverName> <port> <mech>");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) throws IOException {
        s.close();
        System.exit(status);
    }
}
