import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Map;

public class HttpCommunicator {

    private final HttpClient httpClient;

    public HttpCommunicator() {
        this.httpClient = HttpClient.newBuilder()
                .followRedirects(HttpClient.Redirect.ALWAYS)
                .build();
    }

    public String sendGetRequest(String uri, Map<String, Object> attributes) {
        boolean optional = attributes != null && attributes.containsKey("optional-option");
        try {
            return readUri(URI.create(uri), optional);
        } catch (IllegalArgumentException e) {
            if (optional) {
                return "";
            }
            throw e;
        }
    }


    private String readUri(URI uri, boolean optional) {
        try {
            HttpRequest request = HttpRequest.newBuilder()
                    .uri(uri)
                    .GET()
                    .build();

            HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
            return response.body();

        } catch (Exception e) {
            if (optional) {
                return "";
            }
            throw new RuntimeException("Failed to fetch URI: " + uri, e);
        }
    }

    public boolean isHttpOrHttps(String url) {
        return url.startsWith("http://") || url.startsWith("https://");
    }
}

