import javax.servlet.http.*;
import javax.servlet.*;
import java.io.*;
import java.net.*;

public class Lisplet extends HttpServlet {
    String host;
    int port;

    public void init(ServletConfig config) throws ServletException {
        super.init(config);
        host = config.getInitParameter("host");
        String portString = config.getInitParameter("port");
        port = Integer.parseInt(portString);
    }

    public void doPost(HttpServletRequest req, HttpServletResponse resp) throws IOException {
        Socket socket = new Socket(host, port);
        PushbackInputStream strm = new PushbackInputStream(socket.getInputStream());
        OutputStream out = socket.getOutputStream();
        Writer writer = new OutputStreamWriter(out, "UTF8");

        sendRequest(req, writer);
        writer.flush();

        if (getResponseHeader(req, resp, strm)) {
            transferResponseBody(resp, strm);
        }

        writer.close();
        strm.close();
        socket.close();
    }


    private void transferResponseBody(HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        OutputStream out = resp.getOutputStream();
        int c;
        while ((c = strm.read()) != -1) {
            out.write(c);
        }
        out.flush();
    }

    private boolean getResponseHeader(HttpServletRequest req, HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        String line;
        BufferedReader reader = new BufferedReader(new InputStreamReader(strm, "UTF8"));
        while (!(line = reader.readLine()).equals("")) {
            int i = line.indexOf(':');
            String tag = line.substring(0, i);
            String value = line.substring(i + 2);
            if (tag.equals("Status")) {
                resp.setStatus(Integer.parseInt(value));
            } else {
                resp.setHeader(tag, value);
            }
        }
        return true;
    }

    private void sendRequest(HttpServletRequest req, Writer writer) throws IOException {
        writeTagValuePair(writer, "Method", req.getMethod());
        writeTagValuePair(writer, "URI", req.getRequestURI());
        writeTagValuePair(writer, "Protocol", req.getProtocol());

        java.util.Enumeration headerNames = req.getHeaderNames();
        while (headerNames.hasMoreElements()) {
            String name = (String) headerNames.nextElement();
            String value = req.getHeader(name);
            writeTagValuePair(writer, name, value);
        }
        writer.write("\r\n");

        InputStream in = req.getInputStream();
        int c;
        while ((c = in.read()) != -1) {
            writer.write(c);
        }
    }

    private void writeTagValuePair(Writer writer, String tag, String value) throws IOException {
        writer.write(tag);
        writer.write(": ");
        writer.write(value);
        writer.write("\r\n");
    }
}

