    private ForwardingPrintStream streamOut;
    private OutputFormat outputFormat;
    private boolean failed;
    private int resetToGo;
    private final List<Serializable> delayedFrames = new LinkedList<>();
    private boolean inFrame;

    public BinaryLinkClient(String hostName, int hostPort) throws IOException {
        clientSocket = new Socket(hostName, hostPort);
        oos = new ObjectOutputStream(clientSocket.getOutputStream());
        ois = new ObjectInputStream(clientSocket.getInputStream());
        streamOut = new ForwardingPrintStream(false);
        streamErr = new ForwardingPrintStream(true);
        outputFormat = new OutputFormat();
    }

    public void pushFrame(Serializable frame) {
        synchronized (lock) {
            if (failed) return;
            if (inFrame) {
                delayedFrames.add(frame);
                return;
            }
            inFrame = true;
            try {
                oos.writeObject(frame);
                oos.flush();
                if (--resetToGo == 0) {
                    resetToGo = 200;
                    oos.reset();
                }
                while (!delayedFrames.isEmpty()) {
                    pushFrame(delayedFrames.remove(0));
                }
            } catch (IOException e) {
                failed = true;
            } finally {
                inFrame = false;
            }
        }
    }


    public Object readFrame() throws IOException, ClassNotFoundException {
        synchronized (lock) {
            if (failed) throw new IOException("Connection failed");
            return ois.readObject();
        }
    }

    public void close() throws IOException {
        synchronized (lock) {
            if (clientSocket != null) {
                clientSocket.close();
                clientSocket = null;
            }
        }
