import java.net.Socket;
import java.util.concurrent.BlockingQueue;
import java.net.InetSocketAddress;

class MiniCapInputSocketThread extends Thread {
    private Logger log = LoggerFactory.getLogger(MiniCapInputSocketThread.class);
    private IDevice iDevice;
    private BlockingQueue<byte[]> dataQueue;
    private MiniCapLocalThread miniCapPro;
    private AndroidTestTaskBootThread androidTestTaskBootThread;
    private Session session;

    public MiniCapInputSocketThread(IDevice iDevice, BlockingQueue<byte[]> dataQueue, MiniCapLocalThread miniCapPro, Session session) {
        this.iDevice = iDevice;
        this.dataQueue = dataQueue;
        this.miniCapPro = miniCapPro;
        this.session = session;
    }

    @Override
    public void run() {
        String forwardPort = this.miniCapPro.getForwardPort();
        try (Socket socket = new Socket()) {
            socket.connect(new InetSocketAddress("127.0.0.1", Integer.parseInt(forwardPort)));
            DataInputStream dis = new DataInputStream(socket.getInputStream());
            byte[] header = new byte[12];
            while (true) {
                if (Thread.currentThread().isInterrupted()) {
                    break;
                }
                dis.readFully(header);
                int realWidth = header[4] + (header[5] << 8) + (header[6] << 16) + (header[7] << 24);
                int realHeight = header[8] + (header[9] << 8) + (header[10] << 16) + (header[11] << 24);
                int virtualWidth = realWidth;
                int virtualHeight = realHeight;
                int size = virtualWidth * virtualHeight * 4;
                byte[] frame = new byte[size];
                dis.readFully(frame);
                dataQueue.put(frame);
            }
        } catch (IOException | InterruptedException e) {
            log.error("MiniCapInputSocketThread exception: ", e);
        } finally {
            if (session != null) {
                session.close(new CloseStatus(1000, "Client Closed"));
            }
            if (androidTestTaskBootThread != null) {
                androidTestTaskBootThread.stopProcess();
            }
        }
    }

    public void setAndroidTestTaskBootThread(AndroidTestTaskBootThread androidTestTaskBootThread) {
        this.androidTestTaskBootThread = androidTestTaskBootThread;
    }
