        this.mServerIpAddress = serverIpAddress;
        this.mPort = port;
    }

    public void setOnSendListener(OnSendListener onSendListener) {
        this.mOnSendListener = onSendListener;
    }

    @Override
    public void run() {
        try {
            init();
            if (mOnSendListener != null) {
                mOnSendListener.onStart();
            }
            parseHeader();
            parseBody();
            if (mOnSendListener != null) {
                mOnSendListener.onSuccess(mFileInfo);
            }
        } catch (IOException e) {
            if (mOnSendListener != null) {
                mOnSendListener.onFailure(e, mFileInfo);
            }
        } finally {
            finish();
        }
    }

    private void init() throws IOException {
        mSocket = new Socket(mServerIpAddress, mPort);
        mOutputStream = new BufferedOutputStream(mSocket.getOutputStream());
    }

    private void parseHeader() throws IOException {
        DataOutputStream dataOutputStream = new DataOutputStream(mOutputStream);
        dataOutputStream.writeUTF(mFileInfo.getFileName());
        dataOutputStream.writeLong(mFileInfo.getFileSize());
        dataOutputStream.flush();
    }

    private void parseBody() throws IOException {
        FileInputStream fileInputStream = new FileInputStream(mFileInfo.getFile());
        byte[] buffer = new byte[BUFFER_SIZE];
        int len;
        long totalSent = 0;
        while ((len = fileInputStream.read(buffer)) != -1) {
            mOutputStream.write(buffer, 0, len);
            totalSent += len;
            if (mOnSendListener != null) {
                mOnSendListener.onProgress(totalSent, mFileInfo.getFileSize());
            }
        }
        mOutputStream.flush();
        fileInputStream.close();
    }


    private void finish() {
        try {
            if (mOutputStream != null) {
                mOutputStream.close();
            }
            if (mSocket != null) {
                mSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }


    public interface OnSendListener {
        void onStart();
        void onProgress(long sentBytes, long totalBytes);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable throwable, FileInfo fileInfo);
    }

}

