public class ProtobufIO {

    private Socket sock;
    private InputStream inputStream;
    private OutputStream outputStream;
    private Thread thread;
    private volatile boolean shutdown = false;
    private PrintStream outStream;
    private List<MessageCallback> callbacks = Collections.synchronizedList(new ArrayList<>());
    private AtomicLong apiCallIdGen = new AtomicLong(1);
    private final Object CALLBACK_LOCK = new Object();


    public ProtobufIO(String host, int port, PrintStream out, String threadName) throws IOException {
        this.sock = new Socket(host, port);
        this.inputStream = sock.getInputStream();
        this.outputStream = sock.getOutputStream();
        this.outStream = out;
        this.thread = new Thread(new ProtobufIOWorker(), threadName);
        this.thread.start();
    }

    public long send(String apiCall, MessageLite msg) throws IOException {
        long apiCallId = getNextApiCallId();
        send(apiCallId, apiCall, msg);
        return apiCallId;
    }


    public long send(long apiCallIdRef, String apiCallRef, MessageLite... messages) throws IOException {
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        DataOutputStream dos = new DataOutputStream(baos);

        dos.writeLong(apiCallIdRef);
        dos.writeUTF(apiCallRef);
        dos.writeInt(messages.length);

        for (MessageLite msg : messages) {
            byte[] msgBytes = msg.toByteArray();
            dos.writeInt(msgBytes.length);
            dos.write(msgBytes);
        }

        send(baos.toByteArray());
        return apiCallIdRef;
    }

    public synchronized void send(byte[] data) throws IOException {
        DataOutputStream dos = new DataOutputStream(outputStream);
        dos.writeInt(data.length);
        dos.write(data);
        dos.flush();
    }

    public void registerCallback(MessageCallback callback) {
        callbacks.add(callback);
    }

    public void unregisterCallback(MessageCallback callback) {
        callbacks.remove(callback);
    }

    public long getNextApiCallId() {
        return apiCallIdGen.getAndIncrement();
    }

    public void shutdown() throws IOException {
        shutdown = true;
        if (thread != null) {
            thread.interrupt();
        }
        if (sock != null) {
            sock.close();
        }
    }

    private void callback(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap) {
        synchronized (CALLBACK_LOCK) {
            for (MessageCallback callback : callbacks) {
                callback.onMessage(apiCallId, retCode, message, cause, correction, details, objRefsMap);
            }
        }
    }


    public interface MessageCallback {
        void onMessage(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap);
    }

    private class ProtobufIOWorker implements Runnable {
        @Override
        public void run() {
            try {
                DataInputStream dis = new DataInputStream(inputStream);
                while (!shutdown) {
                    int length = dis.readInt();
                    byte[] data = new byte[length];
                    dis.readFully(data);

                    DataInputStream dataInputStream = new DataInputStream(new ByteArrayInputStream(data));
                    long apiCallId = dataInputStream.readLong();
                    long retCode = dataInputStream.readLong();
                    String message = dataInputStream.readUTF();
                    String cause = dataInputStream.readUTF();
                    String correction = dataInputStream.readUTF();
                    String details = dataInputStream.readUTF();

                    int mapSize = dataInputStream.readInt();
                    Map<String, String> objRefsMap = new HashMap<>();
                    for (int i = 0; i < mapSize; i++) {
                        objRefsMap.put(dataInputStream.readUTF(), dataInputStream.readUTF());
                    }
                    callback(apiCallId, retCode, message, cause, correction, details, objRefsMap);

                }
            } catch (IOException e) {
                if (!shutdown && outStream != null) {
                    outStream.println("Error in ProtobufIOWorker: " + e.getMessage());
                }
            }
        }
    }
}

