    private HttpClient httpClient;

    public HttpCommunicationClient(InetSocketAddress address) {
        this.address = address;
    }

    public void setupHttpClient(ThreadPool threadPool) {
        this.httpClient = HttpClient.newBuilder()
                .executor(threadPool)
                .followRedirects(HttpClient.Redirect.ALWAYS)
                .connectTimeout(Duration.ofSeconds(5))
                .build();
    }


    public void closeClient() {
        if (this.httpClient != null) {
             // No direct way to close HttpClient in Java 11. Resource cleanup handled by executor shutdown.
        }
    }

    private HttpResponse<String> executeAndDefaultAssertions(HttpRequest request) throws IOException, InterruptedException {
        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        return response;
    }

    public HttpResponse<String> get(String path, String[]... headers) throws IOException, InterruptedException {
        HttpRequest.Builder builder = HttpRequest.newBuilder()
                .uri(URI.create("http://" + address.getHostName() + ":" + address.getPort() + path))
                .GET();
        for (String[] header : headers) {
            builder.header(header[0], header[1]);
        }
        return executeAndDefaultAssertions(builder.build());
    }


    public HttpResponse<String> browserGet(String uri) throws IOException, InterruptedException {
        return get(uri, browserHeader());
    }

    public HttpResponse<String> post(String path) throws IOException, InterruptedException {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("http://" + address.getHostName() + ":" + address.getPort() + path))
                .POST(HttpRequest.BodyPublishers.noBody())
                .build();
        return executeAndDefaultAssertions(request);
    }


    public List<String> getAllRedirectLocations(String path, String[]... headers) throws IOException, InterruptedException {
        List<String> redirectLocations = new ArrayList<>();
        HttpClient redirectClient = HttpClient.newBuilder()
                .followRedirects(HttpClient.Redirect.NORMAL)
                .executor(Executors.newFixedThreadPool(1)) // Use a small thread pool for redirects
                .build();

        HttpRequest.Builder builder = HttpRequest.newBuilder()
                .uri(URI.create("http://" + address.getHostName() + ":" + address.getPort() + path))
                .GET();
        for (String[] header : headers) {
            builder.header(header[0], header[1]);
        }

        HttpResponse<String> response = redirectClient.send(builder.build(), HttpResponse.BodyHandlers.ofString());

        while (response.previousResponse().isPresent()) {
            redirectLocations.add(response.previousResponse().get().uri().toString());
            response = response.previousResponse().get();
        }
        return redirectLocations;
    }


    public static String[] browserHeader() {
        return new String[]{"User-Agent", "Mozilla/5.0"};
    }
}

