    public AsynchronousSocketChannelStream(ServerAddress serverAddress, InetAddressResolver inetAddressResolver, SocketSettings settings, AsynchronousChannelGroup group) {
        this.serverAddress = serverAddress;
        this.inetAddressResolver = inetAddressResolver;
        this.settings = settings;
        this.group = group;
    }

    public void openAsync(OperationContext operationContext, AsyncCompletionHandler<Void> handler) {
        Queue<SocketAddress> socketAddressQueue = inetAddressResolver.resolve(serverAddress);
        initializeSocketChannel(handler, socketAddressQueue);
    }

    private void initializeSocketChannel(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue) {
        try {
            AsynchronousSocketChannel attemptConnectionChannel = group == null ? AsynchronousSocketChannel.open() : AsynchronousSocketChannel.open(group);
            attemptConnectionChannel.connect(socketAddressQueue.poll(), new OpenCompletionHandler(handler, socketAddressQueue, attemptConnectionChannel), null);
        } catch (IOException e) {
            handler.failed(e, null);
        }
    }


    private class OpenCompletionHandler implements CompletionHandler<Void, Object> {
        private final AsyncCompletionHandler<Void> handler;
        private final Queue<SocketAddress> socketAddressQueue;
        private final AsynchronousSocketChannel attemptConnectionChannel;

        OpenCompletionHandler(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue, AsynchronousSocketChannel attemptConnectionChannel) {
            this.handler = handler;
            this.socketAddressQueue = socketAddressQueue;
            this.attemptConnectionChannel = attemptConnectionChannel;
        }

        @Override
        public void completed(Void result, Object attachment) {
            try {
                attemptConnectionChannel.setOption(StandardSocketOptions.SO_KEEPALIVE, settings.isKeepAlive());
                attemptConnectionChannel.setOption(StandardSocketOptions.TCP_NODELAY, settings.isTcpNoDelay());
                handler.completed(null, new AsynchronousSocketChannelAdapter(attemptConnectionChannel));
            } catch (IOException e) {
                failed(e, attachment);
            }
        }

        @Override
        public void failed(Throwable exc, Object attachment) {
            try {
                attemptConnectionChannel.close();
            } catch (IOException ignored) {}

            if (!socketAddressQueue.isEmpty()) {
                initializeSocketChannel(handler, socketAddressQueue);
            } else {
                handler.failed(exc, attachment);
            }
        }
    }

    private class AsynchronousSocketChannelAdapter implements ExtendedAsynchronousByteChannel {
        private final AsynchronousSocketChannel channel;

        AsynchronousSocketChannelAdapter(AsynchronousSocketChannel channel) {
            this.channel = channel;
        }

        @Override
        public Future<Integer> read(ByteBuffer dst) {
            return channel.read(dst);
        }

        @Override
        public <A> void read(ByteBuffer dst, long timeout, TimeUnit unit, A attachment, CompletionHandler<Integer,? super A> handler) {
            channel.read(dst, timeout, unit, attachment, handler);
        }


        @Override
        public Future<Integer> write(ByteBuffer src) {
            return channel.write(src);
        }

        @Override
        public <A> void write(ByteBuffer src, long timeout, TimeUnit unit, A attachment, CompletionHandler<Integer,? super A> handler) {
            channel.write(src, timeout, unit, attachment, handler);
        }

        @Override
        public boolean isOpen() {
            return channel.isOpen();
        }

        @Override
        public void close() throws IOException {
            channel.close();
        }
    }


    interface ExtendedAsynchronousByteChannel extends AutoCloseable {
        Future<Integer> read(ByteBuffer dst);
        <A> void read(ByteBuffer dst, long timeout, TimeUnit unit, A attachment, CompletionHandler<Integer,? super A> handler);
        Future<Integer> write(ByteBuffer src);
        <A> void write(ByteBuffer src, long timeout, TimeUnit unit, A attachment, CompletionHandler<Integer,? super A> handler);
        boolean isOpen();
    }

    interface AsyncCompletionHandler<T> {
        void completed(T result, Object attachment);
        void failed(Throwable exc, Object attachment);
    }

    static class ServerAddress {}
    static class InetAddressResolver {
        public Queue<SocketAddress> resolve(ServerAddress serverAddress) {return null;}
    }
    static class SocketSettings {
        public boolean isKeepAlive() {return true;}
        public boolean isTcpNoDelay() {return true;}
    }
    static class OperationContext {}
}

