import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.ByteBuffer;
import java.nio.channels.SocketChannel;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;
import java.util.zip.CRC32;

class Packet {
    byte[] data;

    Packet(byte[] data) {
        this.data = data;
    }
}

public class ClientController implements Runnable {

    private BlockingQueue<Packet> inner = new LinkedBlockingQueue<>();
    private final Object lock = new Object();
    private final InetSocketAddress remote;
    private Thread thread;
    private SocketChannel channel;

    public ClientController(String host, int port) {
        this.remote = new InetSocketAddress(host, port);
    }

    public void start() {
        synchronized (lock) {
            if (thread == null || !thread.isAlive()) {
                thread = new Thread(this);
                thread.start();
            }
        }
    }

    public boolean put(Packet packet) {
        return inner.offer(packet);
    }


    public void clear() {
        inner.clear();
    }

    @Override
    public void run() {
        try {
            channel = SocketChannel.open();
            channel.configureBlocking(false);
            channel.connect(remote);

            while (!Thread.currentThread().isInterrupted()) {
                if (!channel.finishConnect()) {
                    Thread.sleep(10); // Short delay to avoid busy-waiting
                    continue;
                }

                write();
            }

        } catch (IOException | InterruptedException e) {
            e.printStackTrace(); // Handle exceptions appropriately
        } finally {
            try {
                if (channel != null) {
                    channel.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void write() throws IOException, InterruptedException {
        Packet packet = inner.take();
        if (packet != null && channel.isConnected()) {
            CRC32 crc = new CRC32();
            crc.update(packet.data);
            long checksum = crc.getValue();

            ByteBuffer buffer = ByteBuffer.allocate(packet.data.length + 8);
            buffer.putLong(checksum);
            buffer.put(packet.data);
            buffer.flip();

            while (buffer.hasRemaining()) {
                channel.write(buffer);
            }
        }
    }
}

