import org.slf4j.Logger;
import java.io.IOException;
import java.net.SocketAddress;
import java.nio.channels.ClosedChannelException;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.Iterator;
import java.util.List;
import java.util.Set;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.Semaphore;
import java.util.concurrent.TimeUnit;

public class ConnectionWorker implements Runnable {

    private final Logger logger;
    private volatile boolean stop = false;
    private final Selector selector;
    private final BlockingQueue<ConnectTask> myConnectQueue;
    private final BlockingQueue<WriteTask> myWriteQueue;
    private final Semaphore mySemaphore;
    private final List<KeyProcessor> myKeyProcessors;
    private final TimeService myTimeService;
    private long lastCleanupTime;
    private final int mySelectorTimeoutMillis;
    private final long myCleanupTimeoutMillis;
    private final CleanupProcessor myCleanupProcessor;
    private final NewConnectionAllower myNewConnectionAllower;


    public ConnectionWorker(Logger logger, Selector selector, BlockingQueue<ConnectTask> connectQueue,
                           BlockingQueue<WriteTask> writeQueue, Semaphore semaphore,
                           List<KeyProcessor> keyProcessors, TimeService timeService, int selectorTimeoutMillis,
                           long cleanupTimeoutMillis, CleanupProcessor cleanupProcessor,
                           NewConnectionAllower newConnectionAllower) {

        this.logger = logger;
        this.selector = selector;
        this.myConnectQueue = connectQueue;
        this.myWriteQueue = writeQueue;
        this.mySemaphore = semaphore;
        this.myKeyProcessors = keyProcessors;
        this.myTimeService = timeService;
        this.mySelectorTimeoutMillis = selectorTimeoutMillis;
        this.myCleanupTimeoutMillis = cleanupTimeoutMillis;
        this.myCleanupProcessor = cleanupProcessor;
        this.myNewConnectionAllower = newConnectionAllower;

    }


    @Override
    public void run() {
        try {
            while (!stop) {
                mySemaphore.acquire();
                try {
                    connectToPeersFromQueue();
                    processWriteTasks();
                    if (needRunCleanup()) {
                        cleanup();
                    }
                    processSelectedKeys();

                } finally {
                    mySemaphore.release();
                }
            }
        } catch (InterruptedException e) {
            logger.info("Connection worker interrupted. Stopping.");
        } catch (Exception e) {
            logger.error("Unexpected exception in connection worker", e);
        }
    }


    private void connectToPeersFromQueue() {
        ConnectTask task;
        while ((task = myConnectQueue.poll()) != null) {
            if (myNewConnectionAllower.isAllowed()) {
                try {
                    SocketChannel channel = SocketChannel.open();
                    channel.configureBlocking(false);
                    channel.connect(task.getSocketAddress());
                    channel.register(selector, SelectionKey.OP_CONNECT, task.getAttachment());
                } catch (IOException e) {
                    logger.warn("Failed to connect to peer: " + task.getSocketAddress(), e);
                }
            }
        }
    }


    private void processSelectedKeys() throws IOException {
        int readyChannels = selector.select(mySelectorTimeoutMillis);
        if (readyChannels > 0) {
            Set<SelectionKey> selectedKeys = selector.selectedKeys();
            Iterator<SelectionKey> keyIterator = selectedKeys.iterator();
            while (keyIterator.hasNext()) {
                SelectionKey key = keyIterator.next();
                keyIterator.remove();
                processSelectedKey(key);
            }
        }
    }

    private void processSelectedKey(SelectionKey key) {
        try {
            if (!key.isValid()) return;

            for (KeyProcessor processor : myKeyProcessors) {
                if (processor.accept(key)) {
                    processor.process(key);
                    break;
                }
            }
        } catch (ClosedChannelException e) {
            logger.debug("Channel closed", e);
            key.cancel();
        } catch (Exception e) {
            logger.warn("Error processing key", e);
            key.cancel();
        }
    }


    public <T> boolean offerConnect(ConnectTask task, int timeout, TimeUnit unit) {
        return addTaskToQueue(task, timeout, unit, myConnectQueue);
    }

    private <T> boolean addTaskToQueue(T task, int timeout, TimeUnit unit, BlockingQueue<T> queue) {
        try {
            return queue.offer(task, timeout, unit);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return false;
        }
    }

    private void processWriteTasks() {
        WriteTask task;
        while ((task = myWriteQueue.poll()) != null) {
            SelectionKey key = task.getChannel().keyFor(selector);
            if (key != null && key.isValid()) {
                key.interestOps(key.interestOps() | SelectionKey.OP_WRITE);
            }
        }
    }


    private void cleanup() {
        myCleanupProcessor.cleanup(selector);
        lastCleanupTime = myTimeService.getCurrentTimeMillis();
    }

    private boolean needRunCleanup() {
        return myTimeService.getCurrentTimeMillis() - lastCleanupTime > myCleanupTimeoutMillis;
    }


    public interface KeyProcessor {
        boolean accept(SelectionKey key);
        void process(SelectionKey key) throws IOException;
    }

    public interface TimeService {
        long getCurrentTimeMillis();
    }

    public interface CleanupProcessor {
        void cleanup(Selector selector);
    }

    public interface NewConnectionAllower {
        boolean isAllowed();
    }

    public static class ConnectTask {
        private final SocketAddress socketAddress;
        private final Object attachment;

        public ConnectTask(SocketAddress socketAddress, Object attachment) {
            this.socketAddress = socketAddress;
            this.attachment = attachment;
        }

        public SocketAddress getSocketAddress() {
            return socketAddress;
        }

        public Object getAttachment() {
            return attachment;
        }
    }

    public static class WriteTask {
        private final SocketChannel channel;
        private final Object attachment;

        public WriteTask(SocketChannel channel, Object attachment) {
            this.channel = channel;
            this.attachment = attachment;
        }


        public SocketChannel getChannel() {
            return channel;
        }

        public Object getAttachment() {
            return attachment;
        }
    }
}

