import org.apache.kafka.clients.admin.AdminClient;
import org.apache.kafka.clients.admin.Config;
import org.apache.kafka.clients.admin.DescribeConfigsResult;
import org.apache.kafka.common.config.ConfigResource;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.time.Duration;
import java.util.*;
import java.util.concurrent.ExecutionException;

public class KafkaAdminTopicConfigProvider {

    private final Properties _clusterConfigs;
    private final AdminClient _adminClient;
    private static final Duration DESCRIBE_CLUSTER_CONFIGS_TIMEOUT = Duration.ofSeconds(90);
    private static final Logger LOG = LoggerFactory.getLogger(KafkaAdminTopicConfigProvider.class);


    public KafkaAdminTopicConfigProvider(Properties clusterConfigs) {
        _clusterConfigs = clusterConfigs;
        _adminClient = AdminClient.create(clusterConfigs);
    }

    public Properties topicConfigs(String topic) {
        return topicConfigs(Collections.singleton(topic)).getOrDefault(topic, new Properties());
    }


    public Map<String, Properties> topicConfigs(Set<String> topics) {
        Map<String, Properties> topicConfigsMap = new HashMap<>();
        Collection<ConfigResource> resources = new ArrayList<>();
        topics.forEach(topic -> resources.add(new ConfigResource(ConfigResource.Type.TOPIC, topic)));

        try {
            DescribeConfigsResult describeConfigsResult = _adminClient.describeConfigs(resources);
            Map<ConfigResource, Config> configs = describeConfigsResult.all().get();

            configs.forEach((resource, config) -> topicConfigsMap.put(resource.name(), convertConfigToProperties(config)));

        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve topic configs for topics: {}. Returning empty map.", topics, e);
            return Collections.emptyMap();
        }
        return topicConfigsMap;

    }

    public Map<String, Properties> allTopicConfigs() {
        try {
            Set<String> allTopics = _adminClient.listTopics(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT).names().get();
            return topicConfigs(allTopics);
        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve all topic configs. Returning empty map.", e);
            return Collections.emptyMap();
        }
    }

    private Properties convertConfigToProperties(Config config) {
        Properties properties = new Properties();
        config.entries().stream().filter(entry -> entry.value() != null).forEach(entry -> properties.setProperty(entry.name(), entry.value()));
        return properties;
    }
}

