import java.util.concurrent.TimeUnit;
import java.util.concurrent.TimeoutException;

public class KafkaTopicAdmin implements AutoCloseable {

    private final Map<String, Object> configs;
    private final int operationTimeout;

    public KafkaTopicAdmin(Map<String, Object> configs, int operationTimeout) {
        this.configs = configs;
        this.operationTimeout = operationTimeout;
    }

    public Map<String, TopicDescription> describeTopics(String... topicNames) {
        try (Admin admin = createAdmin()) {
            DescribeTopicsResult result = admin.describeTopics(Collections.singletonList(topicNames[0]));
            KafkaFuture<Map<String, TopicDescription>> future = result.allTopicNames();
            return future.get(operationTimeout, TimeUnit.SECONDS);
        } catch (InterruptedException | ExecutionException | TimeoutException e) {
            throw new KafkaException("Error describing topics", e);
        }
    }


    protected Admin createAdmin() {
        return Admin.create(getAdminConfig());
    }

    protected Map<String, Object> getAdminConfig() {
        Map<String, Object> adminConfig = new HashMap<>(configs);
        if (!adminConfig.containsKey(AdminClientConfig.REQUEST_TIMEOUT_MS_CONFIG)) {
            adminConfig.put(AdminClientConfig.REQUEST_TIMEOUT_MS_CONFIG, operationTimeout * 1000);
        }
        return adminConfig;
    }

    @Override
    public void close() throws Exception {
        // No resources to explicitly close in this implementation as AdminClient handles it.
    }
}
