    private AsyncControl asyncControl;


    public MilkmanWebsocketClient(URI serverUri, Flow.Subscriber<byte[]> responseSubscriber, AsyncControl asyncControl) {
        super(serverUri);
        this.responseSubscriber = responseSubscriber;
        this.asyncControl = asyncControl;
    }

    @Override
    public void onOpen(ServerHandshake handshakedata) {
        asyncControl.setRequestReady();
    }

    @Override
    public void send(String message) {
        super.send(message);
        if (responseSubscriber != null) {
            responseSubscriber.onNext(message.getBytes());
        }
    }

    public void send(byte[] message) {
        super.send(message);
        if (responseSubscriber != null) {
            responseSubscriber.onNext(message);
        }
    }


    @Override
    public void onMessage(String message) {
        if (responseSubscriber != null) {
            responseSubscriber.onNext(message.getBytes());
        }
    }

    @Override
    public void onMessage(ByteBuffer message) {
        byte[] array = new byte[message.remaining()];
        message.get(array);
        if (responseSubscriber != null) {
            responseSubscriber.onNext(array);
        }
    }


    @Override
    public void onClose(int code, String reason, boolean remote) {
        if (responseSubscriber != null) {
            responseSubscriber.onComplete();
        }
        asyncControl.setRequestSuccessful();
    }

    @Override
    public void onError(Exception ex) {
        if (responseSubscriber != null) {
            responseSubscriber.onError(ex);
        }
        asyncControl.setRequestFailed(ex);
    }


    public interface AsyncControl {
        void setRequestReady();
        void setRequestSuccessful();
        void setRequestFailed(Throwable t);
    }
